INSERT INTO `articles` VALUES ('6900733850540834830', 'üéâ Element UI for Vue 3.0 Êù•‰∫ÜÔºÅ', 'https://juejin.cn/post/6900733850540834830', '<div class=\"markdown-body\"><style>.markdown-body{word-break:break-word;line-height:1.75;font-weight:400;font-size:15px;overflow-x:hidden;color:#333}.markdown-body h1,.markdown-body h2,.markdown-body h3,.markdown-body h4,.markdown-body h5,.markdown-body h6{line-height:1.5;margin-top:35px;margin-bottom:10px;padding-bottom:5px}.markdown-body h1{font-size:30px;margin-bottom:5px}.markdown-body h2{padding-bottom:12px;font-size:24px;border-bottom:1px solid #ececec}.markdown-body h3{font-size:18px;padding-bottom:0}.markdown-body h4{font-size:16px}.markdown-body h5{font-size:15px}.markdown-body h6{margin-top:5px}.markdown-body p{line-height:inherit;margin-top:22px;margin-bottom:22px}.markdown-body img{max-width:100%}.markdown-body hr{border:none;border-top:1px solid #ddd;margin-top:32px;margin-bottom:32px}.markdown-body code{word-break:break-word;border-radius:2px;overflow-x:auto;background-color:#fff5f5;color:#ff502c;font-size:.87em;padding:.065em .4em}.markdown-body code,.markdown-body pre{font-family:Menlo,Monaco,Consolas,Courier New,monospace}.markdown-body pre{overflow:auto;position:relative;line-height:1.75}.markdown-body pre>code{font-size:12px;padding:15px 12px;margin:0;word-break:normal;display:block;overflow-x:auto;color:#333;background:#f8f8f8}.markdown-body a{text-decoration:none;color:#0269c8;border-bottom:1px solid #d1e9ff}.markdown-body a:active,.markdown-body a:hover{color:#275b8c}.markdown-body table{display:inline-block!important;font-size:12px;width:auto;max-width:100%;overflow:auto;border:1px solid #f6f6f6}.markdown-body thead{background:#f6f6f6;color:#000;text-align:left}.markdown-body tr:nth-child(2n){background-color:#fcfcfc}.markdown-body td,.markdown-body th{padding:12px 7px;line-height:24px}.markdown-body td{min-width:120px}.markdown-body blockquote{color:#666;padding:1px 23px;margin:22px 0;border-left:4px solid #cbcbcb;background-color:#f8f8f8}.markdown-body blockquote:after{display:block;content:\"\"}.markdown-body blockquote>p{margin:10px 0}.markdown-body ol,.markdown-body ul{padding-left:28px}.markdown-body ol li,.markdown-body ul li{margin-bottom:0;list-style:inherit}.markdown-body ol li .task-list-item,.markdown-body ul li .task-list-item{list-style:none}.markdown-body ol li .task-list-item ol,.markdown-body ol li .task-list-item ul,.markdown-body ul li .task-list-item ol,.markdown-body ul li .task-list-item ul{margin-top:0}.markdown-body ol ol,.markdown-body ol ul,.markdown-body ul ol,.markdown-body ul ul{margin-top:3px}.markdown-body ol li{padding-left:6px}@media (max-width:720px){.markdown-body h1{font-size:24px}.markdown-body h2{font-size:20px}.markdown-body h3{font-size:18px}}</style><blockquote>\n<p>&#x7B2C;&#x4E00;&#x4E2A;&#x4F7F;&#x7528; TypeScript + Vue 3.0 Composition API &#x91CD;&#x6784;&#x7684;&#x7EC4;&#x4EF6;&#x5E93; Element Plus &#x53D1;&#x5E03;&#x4E86; &#x1F389; ~</p>\n</blockquote>\n<p>2016 &#x5E74; 3 &#x6708; 13 &#x65E5; Element &#x6084;&#x7136;&#x8BDE;&#x751F;&#xFF0C;&#x7ECF;&#x5386;&#x4E86; 4 &#x5E74;&#x7684;&#x98CE;&#x96E8;&#x6D17;&#x793C;&#xFF0C;&#x6211;&#x4EEC;&#x4ECE;&#x4E00;&#x4E2A;&#x997F;&#x4E86;&#x4E48;&#x5185;&#x90E8;&#x4E1A;&#x52A1;&#x7EC4;&#x4EF6;&#x5E93;&#x6210;&#x957F;&#x4E3A; Vue &#x751F;&#x6001;&#x91CC;&#x6700;&#x6D41;&#x884C;&#x7684; UI &#x7EC4;&#x4EF6;&#x5E93;&#x4E4B;&#x4E00;&#x3002;</p>\n<p>&#x622A;&#x81F3;&#x672C;&#x6587;&#x64B0;&#x5199;&#x65F6;&#xFF0C;Element &#x5DF2;&#x83B7;&#x5F97; <strong>48200</strong> Github Star&#xFF0C;&#xA0;NPM &#x4E0B;&#x8F7D;&#x91CF; <strong>95</strong> &#x4E07;&#x6B21;/&#x6BCF;&#x6708;&#x7684;&#x53EF;&#x559C;&#x6210;&#x7EE9;&#x3002;&#x611F;&#x8C22;&#x8D85;&#x8FC7; 530 &#x540D;&#x793E;&#x533A;&#x8D21;&#x732E;&#x8005;&#x7684;&#x53C2;&#x4E0E;&#x7EF4;&#x62A4;&#xFF0C;&#x548C;&#x6211;&#x4EEC;&#x4E00;&#x540C;&#x5B8C;&#x6210;&#x4E86; 4400 &#x6B21; commit &#x7684;&#x66F4;&#x65B0;&#x8FED;&#x4EE3;&#x3002;</p>\n<p><img alt=\"image.png\" class=\"lazyload inited\" data-src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/ae3f219c5cea4070ad492a250898bd48~tplv-k3u1fbpfcp-zoom-1.image\" data-width=\"800\" data-height=\"600\" src=\"data:image/svg+xml;utf8,&lt;?xml version=&quot;1.0&quot;?&gt;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; version=&quot;1.1&quot; width=&quot;800&quot; height=&quot;600&quot;&gt;&lt;/svg&gt;\">\n*Element &#x5F00;&#x53D1;&#x56E2;&#x961F; 40000 Github Star &#x5E86;&#x795D;&#x4F1A;</p>\n<h2 data-id=\"heading-0\">Element Plus for Vue 3.0</h2>\n<p>2020 &#x5E74;&#xFF0C;&#x968F;&#x7740; Vue 3.0 &#x4E0D;&#x65AD;&#x5B8C;&#x5584;&#x548C;&#x53D1;&#x5E03;&#xFF0C;&#x6211;&#x4EEC;&#x4E5F;&#x7D27;&#x5F20;&#x6295;&#x5165;&#x5230; Element &#x5BF9; Vue 3.0 &#x7684;&#x5347;&#x7EA7;&#x9002;&#x914D;&#x5DE5;&#x4F5C;&#x4E2D;&#x3002;&#x5386;&#x7ECF; 6 &#x4E2A;&#x6708;&#x5171;&#x8BA1; 23 &#x4E2A; alpha &#x7248;&#x672C;&#x7684;&#x8FED;&#x4EE3;&#xFF0C;&#x7EC8;&#x4E8E;&#xFF0C;&#x5728;&#x4ECA;&#x5929;&#xFF0C; Element Plus for Vue 3.0 Beta &#x7248;&#x672C;&#x6B63;&#x5F0F;&#x53D1;&#x5E03; &#x1F38A; &#xFF01;</p>\n<p><a href=\"https://github.com/element-plus/element-plus\" target=\"_blank\" rel=\"nofollow noopener noreferrer\">github.com/element-plu&#x2026;</a></p>\n<p>Vue 3.0 &#x7684;&#x5927;&#x7248;&#x672C;&#x5347;&#x7EA7;&#xFF0C;&#x5BF9;&#x751F;&#x6001;&#x7EC4;&#x4EF6;&#x5E93;&#x6765;&#x8BF4;&#x4E5F;&#x662F;&#x4E00;&#x6B21;&#x4E22;&#x6389;&#x5386;&#x53F2;&#x5305;&#x88B1;&#x524D;&#x884C;&#x7684;&#x597D;&#x673A;&#x4F1A;&#xFF0C;&#x6211;&#x4EEC;&#x5927;&#x5200;&#x9614;&#x65A7;&#x5BF9; Element &#x8FDB;&#x884C;&#x4E86;&#x4E00;&#x6B21;&#x6DF1;&#x5EA6;&#x91CD;&#x6784;&#x3002;</p>\n<p><img alt=\"97132438-ed09e200-1781-11eb-9296-6ac6b3eb0ccd.png\" class=\"lazyload inited\" data-src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/6bda613994324068ac76f9ccd80e64d1~tplv-k3u1fbpfcp-zoom-1.image\" data-width=\"800\" data-height=\"600\" src=\"data:image/svg+xml;utf8,&lt;?xml version=&quot;1.0&quot;?&gt;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; version=&quot;1.1&quot; width=&quot;800&quot; height=&quot;600&quot;&gt;&lt;/svg&gt;\"></p>\n<p>Element Plus for Vue 3.0 &#x662F;&#x4E00;&#x4E2A;&#x4F7F;&#x7528; TypeScript + Composition API &#x91CD;&#x6784;&#x7684;&#x5168;&#x65B0;&#x9879;&#x76EE;&#x3002;&#x6211;&#x4EEC;&#x51E0;&#x4E4E;&#x91CD;&#x5199;&#x4E86;&#x6BCF;&#x4E00;&#x884C; Element &#x7684;&#x4EE3;&#x7801;&#xFF0C;&#x7528;&#x6700; Vue 3 &#x7684;&#x65B9;&#x5F0F;&#x5448;&#x73B0;&#x4E86;&#x6700;&#x5B8C;&#x7F8E;&#x7684; Element&#xFF0C;&#x4E3B;&#x8981;&#x6709;&#xFF1A;</p>\n<ul>\n<li>&#x4F7F;&#x7528; TypeScript &#x5F00;&#x53D1;&#xFF0C;&#x63D0;&#x4F9B;&#x5B8C;&#x6574;&#x7684;&#x7C7B;&#x578B;&#x5B9A;&#x4E49;&#x6587;&#x4EF6;</li>\n<li>&#x4F7F;&#x7528; Vue 3.0 Composition API &#x964D;&#x4F4E;&#x8026;&#x5408;&#xFF0C;&#x7B80;&#x5316;&#x903B;&#x8F91;</li>\n<li>&#x4F7F;&#x7528; Vue 3.0 Teleport &#x65B0;&#x7279;&#x6027;&#x91CD;&#x6784;&#x6302;&#x8F7D;&#x7C7B;&#x7EC4;&#x4EF6;</li>\n<li>&#x4F7F;&#x7528; Lerna &#x7EF4;&#x62A4;&#x548C;&#x7BA1;&#x7406;&#x9879;&#x76EE;</li>\n<li>&#x4F7F;&#x7528;&#x66F4;&#x8F7B;&#x91CF;&#x66F4;&#x901A;&#x7528;&#x7684;&#x65F6;&#x95F4;&#x65E5;&#x671F;&#x89E3;&#x51B3;&#x65B9;&#x6848; Day.js</li>\n<li>&#x5347;&#x7EA7;&#x9002;&#x914D; popperjs, async-validator &#x7B49;&#x6838;&#x5FC3;&#x4F9D;&#x8D56;</li>\n<li>&#x5B8C;&#x5584; 52 &#x79CD;&#x56FD;&#x9645;&#x5316;&#x8BED;&#x8A00;&#x652F;&#x6301;</li>\n</ul>\n<p>&#x9664;&#x6B64;&#x4EE5;&#x5916;&#xFF0C;&#x8FD8;&#x6709;&#xFF1A;</p>\n<ul>\n<li>&#x5168;&#x65B0;&#x7684;&#x89C6;&#x89C9;*</li>\n<li>&#x4F18;&#x5316;&#x7684;&#x7EC4;&#x4EF6; API</li>\n<li>&#x66F4;&#x591A;&#x81EA;&#x5B9A;&#x4E49;&#x9009;&#x9879;</li>\n<li>&#x66F4;&#x52A0;&#x8BE6;&#x5C3D;&#x53CB;&#x597D;&#x7684;&#x6587;&#x6863;</li>\n</ul>\n<h3 data-id=\"heading-1\">Q&#xFF1A;Element Plus &#x548C; Element UI &#x662F;&#x4EC0;&#x4E48;&#x5173;&#x7CFB;? &#x4E3A;&#x4EC0;&#x4E48;&#x53C8;&#x4E00;&#x4E2A;&#x65B0;&#x9879;&#x76EE;&#xFF1F;</h3>\n<p>&#x6B63;&#x5982; <code>vue-next</code> &#x4E4B;&#x4E8E; <code>vue</code>&#xFF0C;&#x4E00;&#x6B21; 100% &#x7684;&#x91CD;&#x6784;&#x867D;&#x7136;&#x89E3;&#x51B3;&#x4E86;&#x5F88;&#x591A;&#x5386;&#x53F2;&#x9057;&#x7559;&#x95EE;&#x9898;&#xFF0C;&#x4F46;&#x4E5F;&#x4E0D;&#x53EF;&#x907F;&#x514D;&#x7684;&#x5F15;&#x5165;&#x4E00;&#x4E9B;&#x65B0;&#x7684; bug &#x548C;&#x95EE;&#x9898;&#xFF0C;&#x800C;&#x72EC;&#x7ACB;&#x7684; issue &#x548C; pr &#x533A;&#x53EF;&#x4EE5;&#x51CF;&#x5C11;&#x5927;&#x5BB6;&#x4F7F;&#x7528;&#x548C;&#x53CD;&#x9988;&#x7684;&#x5FC3;&#x667A;&#x6210;&#x672C;&#xFF0C;&#x4E5F;&#x80FD;&#x66F4;&#x52A0;&#x65B9;&#x4FBF;&#x6211;&#x4EEC;&#x5B9A;&#x4F4D;&#x95EE;&#x9898;&#xFF0C;&#x5E76;&#x884C;&#x7EF4;&#x62A4;&#x8FED;&#x4EE3;&#x3002;</p>\n<p>Element will stay with Vue 2.x</p>\n<p>For Vue 3.0, we recommend using Element Plus from the same team</p>\n<p>&#x5177;&#x4F53;&#x53EF;&#x4EE5;&#x53C2;&#x8003; Element &#x7684; README <a href=\"https://github.com/ElemeFE/element/blob/dev/README.md\" target=\"_blank\" rel=\"nofollow noopener noreferrer\">github.com/ElemeFE/ele&#x2026;</a></p>\n<h3 data-id=\"heading-2\">Q: &#x8001; Element &#x9879;&#x76EE;&#x53EF;&#x4EE5;&#x5E73;&#x6ED1;&#x5347;&#x7EA7;&#x5230; Vue 3.0 + Element Plus &#x5417;&#xFF1F;</h3>\n<p>&#x7531;&#x4E8E; Vue 3.0 &#x5347;&#x7EA7;&#x5F15;&#x5165;&#x4E86;&#x90E8;&#x5206; API &#x7684;&#x8C03;&#x6574;&#xFF0C;&#x8001;&#x9879;&#x76EE;&#x7684;&#x5347;&#x7EA7;&#x4E0D;&#x53EF;&#x907F;&#x514D;&#x7684;&#x8981;&#x505A;&#x4E9B;&#x8BB8;&#x6539;&#x52A8;&#x3002;&#x4F46;&#x6211;&#x4EEC;&#x529B;&#x4E89;&#x628A;&#x53D8;&#x66F4;&#x5185;&#x5BB9;&#x505A;&#x5230;&#x53EF;&#x63A7;&#xFF0C;&#x53EA;&#x9700;&#x8981;&#x5F88;&#x5C11;&#x7684;&#x8C03;&#x6574;&#x5C31;&#x80FD;&#x5B8C;&#x6210;&#x9879;&#x76EE;&#x5347;&#x7EA7;&#x3002;&#x5728;&#x4ECA;&#x540E; Vue 3.0 &#x7684;&#x9879;&#x76EE;&#x91CC;&#xFF0C;&#x8FD8;&#x662F;&#x719F;&#x6089;&#x7684; Element &#x7684;&#x914D;&#x65B9;&#x548C;&#x5473;&#x9053;&#x3002;</p>\n<h3 data-id=\"heading-3\">Q: Element UI &#x8FD8;&#x4F1A;&#x7EF4;&#x62A4;&#x5417;&#xFF1F;</h3>\n<p>&#x5F53;&#x7136;&#x4F1A;&#xFF01;(&#x800C;&#x4E14;&#x4E00;&#x76F4;&#x5728;&#x6B63;&#x5E38;&#x8FED;&#x4EE3;&#x53D1;&#x5E03;&#x5440; &#x30FD;(&#x273F;&#xFF9F;&#x25BD;&#xFF9F;)&#x30CE;)</p>\n<p>&#x6BCF;&#x6BCF;&#x770B;&#x5230;&#x793E;&#x533A;&#x7C7B;&#x4F3C;&#x7684;&#x62C5;&#x5FE7;&#xFF0C;&#x5BF9;&#x6211;&#x4EEC;&#x90FD;&#x662F;&#x4E00;&#x79CD;&#x97AD;&#x7B56;&#x3002;&#x4F5C;&#x4E3A;&#x4E00;&#x4E2A;&#x8D1F;&#x8D23;&#x4EFB;&#x7684;&#x5F00;&#x6E90;&#x9879;&#x76EE;&#xFF0C;&#x5FC5;&#x7136;&#x4E0D;&#x4F1A;&#x8F9C;&#x8D1F;&#x5927;&#x5BB6;&#x7684;&#x671F;&#x5F85;&#x3002;&#x968F;&#x7740;&#x7528;&#x6237;&#x7684;&#x589E;&#x591A;&#xFF0C;&#x80A9;&#x4E0A;&#x7684;&#x538B;&#x529B;&#x4E5F;&#x8D8A;&#x6765;&#x8D8A;&#x5927;&#xFF0C;&#x5E0C;&#x671B;&#x5927;&#x5BB6;&#x4F7F;&#x7528;&#x4E86; Element &#x80FD;&#x771F;&#x6B63;&#x4E3A;&#x9879;&#x76EE;&#x5F00;&#x53D1;&#x51CF;&#x8D1F;&#x63D0;&#x6548;&#xFF0C;&#x8FD9;&#x6837;&#x6211;&#x4EEC;&#x591A;&#x52A0;&#x73ED;&#xFF0C;&#x5927;&#x5BB6;&#x65E9;&#x4E0B;&#x73ED;&#xFF0C;&#x60F3;&#x5230;&#x8FD9;&#xFF0C;&#x611F;&#x89C9;&#x80F8;&#x524D;&#x7684;&#x7EA2;&#x9886;&#x5DFE;&#x66F4;&#x9C9C;&#x8273;&#x4E86;&#x1F618;&#x3002;</p>\n<h2 data-id=\"heading-4\">&#x5F00;&#x59CB;&#x4F53;&#x9A8C;&#x5427;</h2>\n<p>&#x6709;&#x5F88;&#x591A;&#x65B9;&#x5F0F;&#x53EF;&#x4EE5;&#x4F53;&#x9A8C; Element Plus for Vue 3.0</p>\n<ul>\n<li>\n<p>&#x624B;&#x52A8;&#x5B89;&#x88C5; <code>npm install element-plus</code> &#xFF0C;&#x8BE6;&#x89C1;&#x5B98;&#x7F51;<a href=\"https://element-plus.org/#/zh-CN/component/installation\" target=\"_blank\" rel=\"nofollow noopener noreferrer\">&#x5B89;&#x88C5;&#x6307;&#x5357;</a></p>\n</li>\n<li>\n<p>&#x4E0B;&#x8F7D;<a href=\"https://github.com/element-plus/element-plus-starter\" target=\"_blank\" rel=\"nofollow noopener noreferrer\"> Webpack &#x811A;&#x624B;&#x67B6;</a> &#x76F4;&#x63A5;&#x8FD0;&#x884C;&#x4F53;&#x9A8C;</p>\n</li>\n<li>\n<p>&#x1F973; &#x5C1D;&#x9C9C;&#x65F6;&#x4E0B;&#x6700;&#x70AB;&#x9177;&#x7684;Vite &#x6253;&#x5305;<a href=\"https://github.com/element-plus/element-plus-vite-starter\" target=\"_blank\" rel=\"nofollow noopener noreferrer\">&#x811A;&#x624B;&#x67B6;</a></p>\n</li>\n<li>\n<p>&#x901A;&#x8FC7;<a href=\"https://github.com/element-plus/vue-cli-plugin-element-plus\" target=\"_blank\" rel=\"nofollow noopener noreferrer\"> Vue CLI &#x63D2;&#x4EF6;</a> &#x5F15;&#x5165;</p>\n</li>\n</ul>\n<h2 data-id=\"heading-5\">What&apos;s&#xA0;Next for Element Plus?</h2>\n<ul>\n<li>&#x4E00;&#x6B21;&#x91CD;&#x5927;&#x89C6;&#x89C9;&#x66F4;&#x65B0;</li>\n<li>2 &#x4E2A;&#x5168;&#x65B0;&#x7EC4;&#x4EF6;&#x7D27;&#x5F20;&#x7814;&#x53D1;&#x4E2D;</li>\n<li>&#x8BBF;&#x95EE;&#x901F;&#x5EA6;&#x66F4;&#x5FEB;&#xFF0C;&#x529F;&#x80FD;&#x66F4;&#x5F3A;&#x5927;&#x7684;&#x5B98;&#x65B9;&#x7F51;&#x7AD9;</li>\n</ul>\n<p>&#x559C;&#x6B22; <a href=\"https://github.com/element-plus/element-plus\" target=\"_blank\" rel=\"nofollow noopener noreferrer\">Element Plus</a> &#x5417;&#xFF1F;&#x6765;&#x70B9;&#x4E2A; <a href=\"https://github.com/element-plus/element-plus/stargazers\" target=\"_blank\" rel=\"nofollow noopener noreferrer\">Star</a> &#x2764;&#xFE0F; &#x652F;&#x6301;&#x9F13;&#x52B1;&#x6211;&#x4EEC;&#x4E00;&#x4E0B;&#x5427; ~</p>\n<p>&#x611F;&#x8C22;&#x5927;&#x5BB6; 4 &#x5E74;&#x4EE5;&#x6765;&#x5BF9; Element &#x7684;&#x5173;&#x6CE8;&#xFF0C;&#x662F;&#x4F60;&#x4EEC;&#x7684;&#x652F;&#x6301;&#x8BA9; Element &#x4E0D;&#x65AD;&#x5B8C;&#x5584;&#xFF0C;&#x529B;&#x4E89;&#x505A;&#x5230;&#x6700;&#x597D;&#x3002;&#x6211;&#x4EEC;&#x4E5F;&#x4F1A;&#x4E0D;&#x5FD8;&#x5F00;&#x6E90;&#x521D;&#x5FC3;&#xFF0C;&#x53CD;&#x54FA;&#x793E;&#x533A;&#xFF0C;&#x672A;&#x6765;&#x5C06;&#x5BF9;&#x793E;&#x533A;&#x66F4;&#x52A0;&#x5F00;&#x653E;&#xFF0C;&#x975E;&#x5E38;&#x6B22;&#x8FCE;&#x70ED;&#x7231;&#x5F00;&#x6E90;&#x7684;&#x4F60;&#x6765;&#x548C;&#x6211;&#x4EEC;&#x4E00;&#x8D77;&#x5B8C;&#x5584; Element Plus &#x2014;&#x2014; &#x8D60;&#x4EBA;&#x73AB;&#x7470;&#xFF0C;&#x624B;&#x6709;&#x4F59;&#x9999;&#xFF0C;&#x5F00;&#x6E90;&#x793E;&#x533A;&#x7684;&#x7EF4;&#x62A4;&#xFF0C;&#x4E0D;&#x4EC5;&#x4EC5;&#x662F;&#x9760;&#x4E00;&#x4E2A;&#x56E2;&#x961F;&#xFF0C;&#x4E00;&#x4E2A;&#x516C;&#x53F8;&#xFF0C;&#x6211;&#x4EEC;&#x76F8;&#x4FE1;&#x662F;&#x9760;&#x6240;&#x6709;&#x70ED;&#x7231;&#x5F00;&#x6E90;&#xFF0C;&#x62E5;&#x6709;&#x5F00;&#x6E90;&#x7CBE;&#x795E;&#xFF0C;&#x70ED;&#x8877;&#x4E8E;&#x5206;&#x4EAB;&#x548C;&#x4EA4;&#x6D41;&#x7684;&#x670B;&#x53CB;&#x4EEC;&#x3002;</p><style>.markdown-body pre,.markdown-body pre>code.hljs{color:#333;background:#f8f8f8}.hljs-comment,.hljs-quote{color:#998;font-style:italic}.hljs-keyword,.hljs-selector-tag,.hljs-subst{color:#333;font-weight:700}.hljs-literal,.hljs-number,.hljs-tag .hljs-attr,.hljs-template-variable,.hljs-variable{color:teal}.hljs-doctag,.hljs-string{color:#d14}.hljs-section,.hljs-selector-id,.hljs-title{color:#900;font-weight:700}.hljs-subst{font-weight:400}.hljs-class .hljs-title,.hljs-type{color:#458;font-weight:700}.hljs-attribute,.hljs-name,.hljs-tag{color:navy;font-weight:400}.hljs-link,.hljs-regexp{color:#009926}.hljs-bullet,.hljs-symbol{color:#990073}.hljs-built_in,.hljs-builtin-name{color:#0086b3}.hljs-meta{color:#999;font-weight:700}.hljs-deletion{background:#fdd}.hljs-addition{background:#dfd}.hljs-emphasis{font-style:italic}.hljs-strong{font-weight:700}</style></div> <div class=\"image-viewer-box\" data-v-78c9b824><!----></div>');
INSERT INTO `articles` VALUES ('6901488402353520647', 'Êï∞ÊçÆÂ∫ìÂÖÅËÆ∏Á©∫ÂÄº (null)ÔºåÂæÄÂæÄÊòØÊÇ≤ÂâßÁöÑÂºÄÂßãÔºà1 ÂàÜÈíüÁ≥ªÂàóÔºâ', 'https://juejin.cn/post/6901488402353520647', '<div class=\"markdown-body\"><style>.markdown-body{word-break:break-word;line-height:1.75;font-weight:400;font-size:15px;overflow-x:hidden;color:#333}.markdown-body h1,.markdown-body h2,.markdown-body h3,.markdown-body h4,.markdown-body h5,.markdown-body h6{line-height:1.5;margin-top:35px;margin-bottom:10px;padding-bottom:5px}.markdown-body h1{font-size:30px;margin-bottom:5px}.markdown-body h2{padding-bottom:12px;font-size:24px;border-bottom:1px solid #ececec}.markdown-body h3{font-size:18px;padding-bottom:0}.markdown-body h4{font-size:16px}.markdown-body h5{font-size:15px}.markdown-body h6{margin-top:5px}.markdown-body p{line-height:inherit;margin-top:22px;margin-bottom:22px}.markdown-body img{max-width:100%}.markdown-body hr{border:none;border-top:1px solid #ddd;margin-top:32px;margin-bottom:32px}.markdown-body code{word-break:break-word;border-radius:2px;overflow-x:auto;background-color:#fff5f5;color:#ff502c;font-size:.87em;padding:.065em .4em}.markdown-body code,.markdown-body pre{font-family:Menlo,Monaco,Consolas,Courier New,monospace}.markdown-body pre{overflow:auto;position:relative;line-height:1.75}.markdown-body pre>code{font-size:12px;padding:15px 12px;margin:0;word-break:normal;display:block;overflow-x:auto;color:#333;background:#f8f8f8}.markdown-body a{text-decoration:none;color:#0269c8;border-bottom:1px solid #d1e9ff}.markdown-body a:active,.markdown-body a:hover{color:#275b8c}.markdown-body table{display:inline-block!important;font-size:12px;width:auto;max-width:100%;overflow:auto;border:1px solid #f6f6f6}.markdown-body thead{background:#f6f6f6;color:#000;text-align:left}.markdown-body tr:nth-child(2n){background-color:#fcfcfc}.markdown-body td,.markdown-body th{padding:12px 7px;line-height:24px}.markdown-body td{min-width:120px}.markdown-body blockquote{color:#666;padding:1px 23px;margin:22px 0;border-left:4px solid #cbcbcb;background-color:#f8f8f8}.markdown-body blockquote:after{display:block;content:\"\"}.markdown-body blockquote>p{margin:10px 0}.markdown-body ol,.markdown-body ul{padding-left:28px}.markdown-body ol li,.markdown-body ul li{margin-bottom:0;list-style:inherit}.markdown-body ol li .task-list-item,.markdown-body ul li .task-list-item{list-style:none}.markdown-body ol li .task-list-item ol,.markdown-body ol li .task-list-item ul,.markdown-body ul li .task-list-item ol,.markdown-body ul li .task-list-item ul{margin-top:0}.markdown-body ol ol,.markdown-body ol ul,.markdown-body ul ol,.markdown-body ul ul{margin-top:3px}.markdown-body ol li{padding-left:6px}@media (max-width:720px){.markdown-body h1{font-size:24px}.markdown-body h2{font-size:20px}.markdown-body h3{font-size:18px}}</style><p>&#x6570;&#x636E;&#x5E93;&#x5B57;&#x6BB5;&#x5141;&#x8BB8;&#x7A7A;&#x503C;&#xFF0C;&#x4F1A;&#x9047;&#x5230;&#x4E00;&#x4E9B;&#x95EE;&#x9898;&#xFF0C;&#x6B64;&#x5904;&#x5305;&#x542B;&#x7684;&#x4E00;&#x4E9B;&#x77E5;&#x8BC6;&#x70B9;&#xFF0C;&#x548C;&#x5927;&#x5BB6;&#x804A;&#x4E00;&#x804A;&#x3002;</p>\n<p><strong>&#x6570;&#x636E;&#x51C6;&#x5907;&#xFF1A;</strong></p>\n<p><em>create table user (</em></p>\n<p><em>id int,</em></p>\n<p><em>name varchar(20),</em></p>\n<p><em>index(id)</em></p>\n<p><em>)engine=innodb;</em></p>\n<p><em>insert into user values(1,&apos;shenjian&apos;);</em></p>\n<p><em>insert into user values(2,&apos;zhangsan&apos;);</em></p>\n<p><em>insert into user values(3,&apos;lisi&apos;);</em></p>\n<p><strong>&#x8BF4;&#x660E;&#xFF1A;</strong></p>\n<p>id &#x4E3A;&#x7D22;&#x5F15;&#xFF0C;&#x975E;&#x552F;&#x4E00; (non unique)&#xFF0C;&#x5141;&#x8BB8;&#x7A7A; (null)&#x3002;</p>\n<p><strong>&#x77E5;&#x8BC6;&#x70B9; 1&#xFF08;&#x70ED;&#x8EAB;&#xFF09;&#xFF1A;</strong> <strong>&#x8D1F;&#x5411;&#x67E5;&#x8BE2;&#x4E0D;&#x80FD;&#x547D;&#x4E2D;&#x7D22;&#x5F15;&#xFF0C;&#x4F1A;&#x5BFC;&#x81F4;&#x5168;&#x8868;&#x626B;&#x63CF;&#x3002;</strong></p>\n<p><img alt class=\"lazyload inited\" data-src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/c4515edc81f94735924d327a433c5850~tplv-k3u1fbpfcp-zoom-1.image\" data-width=\"800\" data-height=\"600\" src=\"data:image/svg+xml;utf8,&lt;?xml version=&quot;1.0&quot;?&gt;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; version=&quot;1.1&quot; width=&quot;800&quot; height=&quot;600&quot;&gt;&lt;/svg&gt;\"></p>\n<p><em>explain select * from user where id!=1;</em></p>\n<p>&#x7D22;&#x5F15;&#x5B57;&#x6BB5; id &#x4E0A;&#x7684;&#x4E0D;&#x7B49;&#x4E8E;&#x67E5;&#x8BE2;&#xFF0C;&#x5982;&#x4E0A;&#x56FE;&#x6240;&#x793A;&#xFF1A;</p>\n<p>&#xFF08;1&#xFF09;type=ALL&#xFF0C;&#x5168;&#x8868;&#x626B;&#x63CF;&#xFF1B;</p>\n<p>&#xFF08;2&#xFF09;rows=3&#xFF0C;&#x5168;&#x8868;&#x53EA;&#x6709; 3 &#x884C;&#xFF1B;</p>\n<p><strong>&#x77E5;&#x8BC6;&#x70B9; 2&#xFF08;&#x5212;&#x91CD;&#x70B9;&#xFF09;&#xFF1A;</strong> <strong>&#x5141;&#x8BB8;&#x7A7A;&#x503C;&#xFF0C;&#x4E0D;&#x7B49;&#x4E8E;</strong> <strong>(!=)</strong> <strong>&#x67E5;&#x8BE2;&#xFF0C;&#x53EF;&#x80FD;&#x5BFC;&#x81F4;&#x4E0D;&#x7B26;&#x5408;&#x9884;&#x671F;&#x7684;&#x7ED3;&#x679C;&#x3002;</strong></p>\n<p><img alt class=\"lazyload inited\" data-src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/a281bfc90f03431d9276dd71556fda8d~tplv-k3u1fbpfcp-zoom-1.image\" data-width=\"800\" data-height=\"600\" src=\"data:image/svg+xml;utf8,&lt;?xml version=&quot;1.0&quot;?&gt;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; version=&quot;1.1&quot; width=&quot;800&quot; height=&quot;600&quot;&gt;&lt;/svg&gt;\"></p>\n<p><em>insert into user(name) values(&apos;wangwu&apos;);</em></p>\n<p>&#x5148;&#x6784;&#x9020;&#x4E00;&#x6761; id &#x4E3A; NULL &#x7684;&#x6570;&#x636E;&#xFF0C;&#x53EF;&#x4EE5;&#x770B;&#x5230;&#x5171;&#x6709; 4 &#x6761;&#x8BB0;&#x5F55;&#x3002;</p>\n<p><em>select * from user where</em> <em>id!=1</em>_;_</p>\n<p>&#x518D;&#x6B21;&#x6267;&#x884C;&#x4E0D;&#x7B49;&#x4E8E;&#x67E5;&#x8BE2;&#x3002;</p>\n<p>&#x4F60;&#x731C;&#x7ED3;&#x679C;&#x96C6;&#x6709;&#x51E0;&#x6761;&#x8BB0;&#x5F55;&#xFF08;&#x5171; 4 &#x6761;&#xFF0C;&#x4E0D;&#x7B49;&#x4E8E;&#x6392;&#x9664; 1 &#x6761;&#xFF09;&#xFF1F;</p>\n<p>&#x7B54;&#x9519;&#x4E86;&#xFF01;</p>\n<p>&#x7ED3;&#x679C;&#x96C6;&#x53EA;&#x6709; 2 &#x6761;&#x8BB0;&#x5F55;&#xFF0C;&#x7A7A;&#x503C;&#x8BB0;&#x5F55;&#x8BB0;&#x5F55;&#x5E76;&#x672A;&#x51FA;&#x73B0;&#x5728;&#x7ED3;&#x679C;&#x96C6;&#x91CC;&#x3002;</p>\n<p><img alt class=\"lazyload inited\" data-src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/05a7155cc05148f081516039cbda2117~tplv-k3u1fbpfcp-zoom-1.image\" data-width=\"800\" data-height=\"600\" src=\"data:image/svg+xml;utf8,&lt;?xml version=&quot;1.0&quot;?&gt;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; version=&quot;1.1&quot; width=&quot;800&quot; height=&quot;600&quot;&gt;&lt;/svg&gt;\"></p>\n<p><em>select * from user where id!=1 or id is null;</em></p>\n<p>&#x5982;&#x679C;&#x60F3;&#x5230;&#x5F97;&#x5230;&#x7B26;&#x5408;&#x9884;&#x671F;&#x7684;&#x7ED3;&#x679C;&#x96C6;&#xFF0C;&#x5FC5;&#x987B;&#x52A0;&#x4E0A;&#x4E00;&#x4E2A; or &#x6761;&#x4EF6;&#x3002;</p>\n<p>_&#x753B;&#x5916;&#x97F3;&#xFF1A;_<em>&#x6076;&#x5FC3;&#x4E0D;&#x6076;&#x5FC3;&#xFF0C;&#x8FD9;&#x4E2A;&#x5927;&#x5751;&#x4F60;&#x8E29;&#x8FC7;&#x6CA1;&#x6709;&#xFF1F;</em></p>\n<p><strong>&#x77E5;&#x8BC6;&#x70B9; 3&#xFF08;&#x9644;&#x52A0;&#xFF09;&#xFF1A;</strong> <strong>&#x67D0;&#x4E9B; or &#x6761;&#x4EF6;&#xFF0C;&#x53C8;&#x53EF;&#x80FD;&#x5BFC;&#x81F4;&#x5168;&#x8868;&#x626B;&#x63CF;&#xFF0C;&#x6B64;&#x65F6;&#x5E94;&#x8BE5;&#x4F18;&#x5316;&#x4E3A; union&#x3002;</strong></p>\n<p><img alt class=\"lazyload inited\" data-src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/c423181b20c540eeb56d5dff95219154~tplv-k3u1fbpfcp-zoom-1.image\" data-width=\"800\" data-height=\"600\" src=\"data:image/svg+xml;utf8,&lt;?xml version=&quot;1.0&quot;?&gt;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; version=&quot;1.1&quot; width=&quot;800&quot; height=&quot;600&quot;&gt;&lt;/svg&gt;\"></p>\n<p><em>explain select * from user where id=1;</em></p>\n<p>&#x7D22;&#x5F15;&#x5B57;&#x6BB5; id &#x4E0A;&#x7684;&#x7B49;&#x503C;&#x67E5;&#x8BE2;&#xFF0C;<strong>&#x80FD;</strong>&#x547D;&#x4E2D;&#x7D22;&#x5F15;&#xFF0C;&#x5982;&#x4E0A;&#x56FE;&#x6240;&#x793A;&#xFF1A;</p>\n<p>&#xFF08;1&#xFF09;type=ref&#xFF0C;&#x8D70;&#x975E;&#x552F;&#x4E00;&#x7D22;&#x5F15;&#xFF1B;</p>\n<p>&#xFF08;2&#xFF09;rows=1&#xFF0C;&#x9884;&#x4F30;&#x626B;&#x63CF; 1 &#x884C;&#xFF1B;</p>\n<p><img alt class=\"lazyload inited\" data-src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/2af4150d23dd47bd93b11c640edb1e2d~tplv-k3u1fbpfcp-zoom-1.image\" data-width=\"800\" data-height=\"600\" src=\"data:image/svg+xml;utf8,&lt;?xml version=&quot;1.0&quot;?&gt;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; version=&quot;1.1&quot; width=&quot;800&quot; height=&quot;600&quot;&gt;&lt;/svg&gt;\"></p>\n<p><em>explain select * from user where id is null;</em></p>\n<p>&#x7D22;&#x5F15;&#x5B57;&#x6BB5; id &#x4E0A;&#x7684; null &#x67E5;&#x8BE2;&#xFF0C;&#x4E5F;<strong>&#x80FD;</strong>&#x547D;&#x4E2D;&#x7D22;&#x5F15;&#xFF0C;&#x5982;&#x4E0A;&#x56FE;&#x6240;&#x793A;&#xFF1A;</p>\n<p>&#xFF08;1&#xFF09;type=ref&#xFF0C;&#x8D70;&#x975E;&#x552F;&#x4E00;&#x7D22;&#x5F15;&#xFF1B;</p>\n<p>&#xFF08;2&#xFF09;rows=1&#xFF0C;&#x9884;&#x4F30;&#x626B;&#x63CF; 1 &#x884C;&#xFF1B;</p>\n<p><img alt class=\"lazyload inited\" data-src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/31322e37001544c0ac8430a1828f4a36~tplv-k3u1fbpfcp-zoom-1.image\" data-width=\"800\" data-height=\"600\" src=\"data:image/svg+xml;utf8,&lt;?xml version=&quot;1.0&quot;?&gt;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; version=&quot;1.1&quot; width=&quot;800&quot; height=&quot;600&quot;&gt;&lt;/svg&gt;\"></p>\n<p><em>explain select * from user where id=1 or id is null;</em></p>\n<p>&#x5982;&#x679C;&#x653E;&#x5230;&#x4E00;&#x4E2A; SQL &#x8BED;&#x53E5;&#x91CC;&#x7528; or &#x67E5;&#x8BE2;&#xFF0C;&#x5219;&#x4F1A;<strong>&#x5168;&#x8868;&#x626B;&#x63CF;</strong>&#xFF0C;&#x5982;&#x4E0A;&#x56FE;&#x6240;&#x793A;&#xFF1A;</p>\n<p>&#xFF08;1&#xFF09;type=ALL&#xFF0C;&#x5168;&#x8868;&#x626B;&#x63CF;&#xFF1B;</p>\n<p>&#xFF08;2&#xFF09;rows=4&#xFF0C;&#x5168;&#x8868;&#x53EA;&#x6709; 4 &#x884C;&#xFF1B;</p>\n<p><img alt class=\"lazyload inited\" data-src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/7ac1096f2a954099bc6063fa98059fba~tplv-k3u1fbpfcp-zoom-1.image\" data-width=\"800\" data-height=\"600\" src=\"data:image/svg+xml;utf8,&lt;?xml version=&quot;1.0&quot;?&gt;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; version=&quot;1.1&quot; width=&quot;800&quot; height=&quot;600&quot;&gt;&lt;/svg&gt;\"></p>\n<p><em>explain select * from user where id=1</em></p>\n<p><em>union</em></p>\n<p><em>select * from user where id is null;</em></p>\n<p>&#x6B64;&#x65F6;&#x5E94;&#x8BE5;&#x4F18;&#x5316;&#x4E3A; union &#x67E5;&#x8BE2;&#xFF0C;&#x53C8;&#x80FD;&#x591F;<strong>&#x547D;&#x4E2D;&#x7D22;&#x5F15;</strong>&#x4E86;&#xFF0C;&#x5982;&#x4E0A;&#x56FE;&#x6240;&#x793A;&#xFF1A;</p>\n<p>&#xFF08;1&#xFF09;type=ref&#xFF0C;&#x8D70;&#x975E;&#x552F;&#x4E00;&#x7D22;&#x5F15;&#xFF1B;</p>\n<p>&#xFF08;2&#xFF09;rows=1&#xFF0C;&#x9884;&#x4F30;&#x626B;&#x63CF; 1 &#x884C;&#xFF1B;</p>\n<p>_&#x753B;&#x5916;&#x97F3;&#xFF1A;_<em>&#x7B2C;&#x4E09;&#x884C;&#x4E34;&#x65F6;&#x8868;&#x7684; ALL&#xFF0C;&#x662F;&#x4E24;&#x6B21;&#x7ED3;&#x679C;&#x96C6;&#x7684;&#x5408;&#x5E76;&#x3002;</em></p>\n<p><strong>&#x603B;&#x7ED3;</strong></p>\n<p>&#xFF08;1&#xFF09;<strong>&#x8D1F;&#x5411;&#x6BD4;&#x8F83;</strong>&#xFF08;&#x4F8B;&#x5982;&#xFF1A;!=&#xFF09;&#x4F1A;&#x5F15;&#x53D1;<strong>&#x5168;&#x8868;&#x626B;&#x63CF;</strong>&#xFF1B;</p>\n<p>&#xFF08;2&#xFF09;&#x5982;&#x679C;&#x5141;&#x8BB8;&#x7A7A;&#x503C;&#xFF0C;<strong>&#x4E0D;&#x7B49;&#x4E8E; (!=) &#x7684;&#x67E5;&#x8BE2;&#xFF0C;&#x4E0D;&#x4F1A;&#x5C06;&#x7A7A;&#x503C;&#x884C; (row) &#x5305;&#x542B;&#x8FDB;&#x6765;</strong>&#xFF0C;&#x6B64;&#x65F6;&#x7684;&#x7ED3;&#x679C;&#x96C6;&#x5F80;&#x5F80;&#x662F;&#x4E0D;&#x7B26;&#x5408;&#x9884;&#x671F;&#x7684;&#xFF0C;&#x6B64;&#x65F6;&#x5F80;&#x5F80;&#x8981;&#x52A0;&#x4E0A;&#x4E00;&#x4E2A; or &#x6761;&#x4EF6;&#xFF0C;&#x628A;&#x7A7A;&#x503C; (is null) &#x7ED3;&#x679C;&#x5305;&#x542B;&#x8FDB;&#x6765;&#xFF1B;</p>\n<p>&#xFF08;3&#xFF09;or &#x53EF;&#x80FD;&#x4F1A;&#x5BFC;&#x81F4;&#x5168;&#x8868;&#x626B;&#x63CF;&#xFF0C;&#x6B64;&#x65F6;&#x53EF;&#x4EE5;&#x4F18;&#x5316;&#x4E3A; union &#x67E5;&#x8BE2;&#xFF1B;</p>\n<p>&#xFF08;4&#xFF09;&#x5EFA;&#x8868;&#x65F6;&#x52A0;&#x4E0A;<strong>&#x9ED8;&#x8BA4; (default) &#x503C;</strong>&#xFF0C;&#x8FD9;&#x6837;&#x80FD;&#x907F;&#x514D;&#x7A7A;&#x503C;&#x7684;&#x5751;&#xFF1B;</p>\n<p>&#xFF08;5&#xFF09;<strong>explain</strong> &#x5DE5;&#x5177;&#x662F;&#x4E00;&#x4E2A;&#x597D;&#x4E1C;&#x897F;&#xFF1B;</p>\n<p>&#x5E0C;&#x671B;&#x5927;&#x5BB6;&#x6709;&#x6536;&#x83B7;&#xFF01;</p>\n<p><em>&#x753B;&#x5916;&#x97F3;&#xFF1A;&#x672C;&#x6587;&#x6D4B;&#x8BD5;&#x4E8E; MySQL5.6&#x3002;</em></p><style>.markdown-body pre,.markdown-body pre>code.hljs{color:#333;background:#f8f8f8}.hljs-comment,.hljs-quote{color:#998;font-style:italic}.hljs-keyword,.hljs-selector-tag,.hljs-subst{color:#333;font-weight:700}.hljs-literal,.hljs-number,.hljs-tag .hljs-attr,.hljs-template-variable,.hljs-variable{color:teal}.hljs-doctag,.hljs-string{color:#d14}.hljs-section,.hljs-selector-id,.hljs-title{color:#900;font-weight:700}.hljs-subst{font-weight:400}.hljs-class .hljs-title,.hljs-type{color:#458;font-weight:700}.hljs-attribute,.hljs-name,.hljs-tag{color:navy;font-weight:400}.hljs-link,.hljs-regexp{color:#009926}.hljs-bullet,.hljs-symbol{color:#990073}.hljs-built_in,.hljs-builtin-name{color:#0086b3}.hljs-meta{color:#999;font-weight:700}.hljs-deletion{background:#fdd}.hljs-addition{background:#dfd}.hljs-emphasis{font-style:italic}.hljs-strong{font-weight:700}</style></div> <div class=\"image-viewer-box\" data-v-78c9b824><!----></div>');
