nt;     <span class=\"hljs-comment\">/*  */</span>\n    <span class=\"hljs-keyword\">uint64_t</span> currentEpoch;  <span class=\"hljs-comment\">/* &#x8868;&#x793A;&#x672C;&#x8282;&#x70B9;&#x5F53;&#x524D;&#x8BB0;&#x5F55;&#x7684;&#x6574;&#x4E2A;&#x96C6;&#x7FA4;&#x7684;&#x7EDF;&#x4E00;&#x7684;epoch&#xFF0C;&#x7528;&#x6765;&#x51B3;&#x7B56;&#x9009;&#x4E3E;&#x6295;&#x7968;&#x7B49;&#xFF0C;&#x4E0E;configEpoch&#x4E0D;&#x540C;&#x7684;&#x662F;&#xFF1A;configEpoch&#x8868;&#x793A;&#x7684;&#x662F;master&#x8282;&#x70B9;&#x7684;&#x552F;&#x4E00;&#x6807;&#x5FD7;&#xFF0C;currentEpoch&#x662F;&#x96C6;&#x7FA4;&#x7684;&#x552F;&#x4E00;&#x6807;&#x5FD7;&#x3002; */</span>\n    <span class=\"hljs-keyword\">uint64_t</span> configEpoch;   <span class=\"hljs-comment\">/* &#x6BCF;&#x4E2A;master&#x8282;&#x70B9;&#x90FD;&#x6709;&#x4E00;&#x4E2A;&#x552F;&#x4E00;&#x7684;configEpoch&#x505A;&#x6807;&#x5FD7;&#xFF0C;&#x5982;&#x679C;&#x548C;&#x5176;&#x4ED6;master&#x8282;&#x70B9;&#x51B2;&#x7A81;&#xFF0C;&#x4F1A;&#x5F3A;&#x5236;&#x81EA;&#x589E;&#x4F7F;&#x672C;&#x8282;&#x70B9;&#x5728;&#x96C6;&#x7FA4;&#x4E2D;&#x552F;&#x4E00; */</span>\n    <span class=\"hljs-keyword\">uint64_t</span> offset;    <span class=\"hljs-comment\">/* &#x4E3B;&#x4ECE;&#x590D;&#x5236;&#x504F;&#x79FB;&#x76F8;&#x5173;&#x4FE1;&#x606F;&#xFF0C;&#x4E3B;&#x8282;&#x70B9;&#x548C;&#x4ECE;&#x8282;&#x70B9;&#x542B;&#x4E49;&#x4E0D;&#x540C; */</span>\n    <span class=\"hljs-keyword\">char</span> sender[CLUSTER_NAMELEN]; <span class=\"hljs-comment\">/* &#x53D1;&#x9001;&#x8282;&#x70B9;&#x7684;&#x540D;&#x79F0; */</span>\n    <span class=\"hljs-keyword\">unsigned</span> <span class=\"hljs-keyword\">char</span> myslots[CLUSTER_SLOTS/<span class=\"hljs-number\">8</span>]; <span class=\"hljs-comment\">/* &#x672C;&#x8282;&#x70B9;&#x8D1F;&#x8D23;&#x7684;slots&#x4FE1;&#x606F;,16384/8&#x4E2A;char&#x6570;&#x7EC4;&#xFF0C;&#x4E00;&#x5171;&#x4E3A;16384bit */</span>\n    <span class=\"hljs-keyword\">char</span> slaveof[CLUSTER_NAMELEN]; <span class=\"hljs-comment\">/* master&#x4FE1;&#x606F;&#xFF0C;&#x5047;&#x5982;&#x672C;&#x8282;&#x70B9;&#x662F;slave&#x8282;&#x70B9;&#x7684;&#x8BDD;&#xFF0C;&#x534F;&#x8BAE;&#x5E26;&#x6709;master&#x4FE1;&#x606F; */</span>\n    <span class=\"hljs-keyword\">char</span> myip[NET_IP_STR_LEN];    <span class=\"hljs-comment\">/* IP */</span>\n    <span class=\"hljs-keyword\">char</span> notused1[<span class=\"hljs-number\">34</span>];  <span class=\"hljs-comment\">/* &#x4FDD;&#x7559;&#x5B57;&#x6BB5; */</span>\n    <span class=\"hljs-keyword\">uint16_t</span> cport;      <span class=\"hljs-comment\">/* &#x96C6;&#x7FA4;&#x7684;&#x901A;&#x4FE1;&#x7AEF;&#x53E3; */</span>\n    <span class=\"hljs-keyword\">uint16_t</span> flags;      <span class=\"hljs-comment\">/* &#x672C;&#x8282;&#x70B9;&#x5F53;&#x524D;&#x7684;&#x72B6;&#x6001;&#xFF0C;&#x6BD4;&#x5982; CLUSTER_NODE_HANDSHAKE&#x3001;CLUSTER_NODE_MEET */</span>\n    <span class=\"hljs-keyword\">unsigned</span> <span class=\"hljs-keyword\">char</span> state; <span class=\"hljs-comment\">/* Cluster state from the POV of the sender */</span>\n    <span class=\"hljs-keyword\">unsigned</span> <span class=\"hljs-keyword\">char</span> mflags[<span class=\"hljs-number\">3</span>]; <span class=\"hljs-comment\">/* &#x672C;&#x6761;&#x6D88;&#x606F;&#x7684;&#x7C7B;&#x578B;&#xFF0C;&#x76EE;&#x524D;&#x53EA;&#x6709;&#x4E24;&#x7C7B;&#xFF1A;CLUSTERMSG_FLAG0_PAUSED&#x3001;CLUSTERMSG_FLAG0_FORCEACK */</span>\n    <span class=\"hljs-class\"><span class=\"hljs-keyword\">union</span> <span class=\"hljs-title\">clusterMsgData</span> <span class=\"hljs-title\">data</span>;</span>\n} clusterMsg;\n<span class=\"copy-code-btn\">&#x590D;&#x5236;&#x4EE3;&#x7801;</span></code></pre>\n<p>clusterMsgData &#x662F;&#x4E00;&#x4E2A; union &#x7ED3;&#x6784;&#x4F53;&#xFF0C;&#x5B83;&#x53EF;&#x4EE5;&#x4E3A; PING&#xFF0C;MEET&#xFF0C;PONG &#x6216;&#x8005; FAIL &#x7B49;&#x6D88;&#x606F;&#x4F53;&#x3002;&#x5176;&#x4E2D;&#x5F53;&#x6D88;&#x606F;&#x4E3A; PING&#x3001;MEET &#x548C; PONG &#x7C7B;&#x578B;&#x65F6;&#xFF0C;ping &#x5B57;&#x6BB5;&#x662F;&#x88AB;&#x8D4B;&#x503C;&#x7684;&#xFF0C;&#x800C;&#x662F; FAIL &#x7C7B;&#x578B;&#x65F6;&#xFF0C;fail &#x5B57;&#x6BB5;&#x662F;&#x88AB;&#x8D4B;&#x503C;&#x7684;&#x3002;</p>\n<pre><code class=\"hljs language-c copyable\" lang=\"c\"><span class=\"hljs-comment\">// &#x6CE8;&#x610F;&#x8FD9;&#x662F; union &#x5173;&#x952E;&#x5B57;</span>\n<span class=\"hljs-class\"><span class=\"hljs-keyword\">union</span> <span class=\"hljs-title\">clusterMsgData</span> {</span>\n    <span class=\"hljs-comment\">/* PING, MEET &#x6216;&#x8005; PONG &#x6D88;&#x606F;&#x65F6;&#xFF0C;ping &#x5B57;&#x6BB5;&#x88AB;&#x8D4B;&#x503C; */</span>\n    <span class=\"hljs-class\"><span class=\"hljs-keyword\">struct</span> {</span>\n        <span class=\"hljs-comment\">/* Array of N clusterMsgDataGossip structures */</span>\n        clusterMsgDataGossip gossip[<span class=\"hljs-number\">1</span>];\n    } ping;\n    <span class=\"hljs-comment\">/*  FAIL &#x6D88;&#x606F;&#x65F6;&#xFF0C;fail &#x88AB;&#x8D4B;&#x503C; */</span>\n    <span class=\"hljs-class\"><span class=\"hljs-keyword\">struct</span> {</span>\n        clusterMsgDataFail about;\n    } fail;\n    <span class=\"hljs-comment\">// .... &#x7701;&#x7565; publish &#x548C; update &#x6D88;&#x606F;&#x7684;&#x5B57;&#x6BB5;</span>\n};\n<span class=\"copy-code-btn\">&#x590D;&#x5236;&#x4EE3;&#x7801;</span></code></pre>\n<p>clusterMsgDataGossip &#x662F; PING&#x3001;PONG &#x548C; MEET &#x6D88;&#x606F;&#x7684;&#x7ED3;&#x6784;&#x4F53;&#xFF0C;&#x5B83;&#x4F1A;&#x5305;&#x62EC;&#x53D1;&#x9001;&#x6D88;&#x606F;&#x8282;&#x70B9;&#x7EF4;&#x62A4;&#x7684;&#x5176;&#x4ED6;&#x8282;&#x70B9;&#x4FE1;&#x606F;&#xFF0C;&#x4E5F;&#x5C31;&#x662F;&#x4E0A;&#x6587;&#x4E2D; clusterState &#x4E2D; nodes &#x5B57;&#x6BB5;&#x5305;&#x542B;&#x7684;&#x4FE1;&#x606F;&#xFF0C;&#x5177;&#x4F53;&#x4EE3;&#x7801;&#x5982;&#x4E0B;&#x6240;&#x793A;&#xFF0C;&#x4F60;&#x4E5F;&#x4F1A;&#x53D1;&#x73B0;&#x4E8C;&#x8005;&#x7684;&#x5B57;&#x6BB5;&#x662F;&#x7C7B;&#x4F3C;&#x7684;&#x3002;</p>\n<pre><code class=\"hljs language-c copyable\" lang=\"c\"><span class=\"hljs-keyword\">typedef</span> <span class=\"hljs-class\"><span class=\"hljs-keyword\">struct</span> {</span>\n	<span class=\"hljs-comment\">/* &#x8282;&#x70B9;&#x7684;&#x540D;&#x5B57;&#xFF0C;&#x9ED8;&#x8BA4;&#x662F;&#x968F;&#x673A;&#x7684;&#xFF0C;MEET&#x6D88;&#x606F;&#x53D1;&#x9001;&#x5E76;&#x5F97;&#x5230;&#x56DE;&#x590D;&#x540E;&#xFF0C;&#x96C6;&#x7FA4;&#x4F1A;&#x4E3A;&#x8BE5;&#x8282;&#x70B9;&#x8BBE;&#x7F6E;&#x6B63;&#x5F0F;&#x7684;&#x540D;&#x79F0;*/</span>\n    <span class=\"hljs-keyword\">char</span> nodename[CLUSTER_NAMELEN]; \n    <span class=\"hljs-keyword\">uint32_t</span> ping_sent; <span class=\"hljs-comment\">/* &#x53D1;&#x9001;&#x8282;&#x70B9;&#x6700;&#x540E;&#x4E00;&#x6B21;&#x7ED9;&#x63A5;&#x6536;&#x8282;&#x70B9;&#x53D1;&#x9001; PING &#x6D88;&#x606F;&#x7684;&#x65F6;&#x95F4;&#x6233;&#xFF0C;&#x6536;&#x5230;&#x5BF9;&#x5E94; PONG &#x56DE;&#x590D;&#x540E;&#x4F1A;&#x88AB;&#x8D4B;&#x503C;&#x4E3A;0 */</span>\n    <span class=\"hljs-keyword\">uint32_t</span> pong_received; <span class=\"hljs-comment\">/* &#x53D1;&#x9001;&#x8282;&#x70B9;&#x6700;&#x540E;&#x4E00;&#x6B21;&#x6536;&#x5230;&#x63A5;&#x6536;&#x8282;&#x70B9;&#x53D1;&#x9001; PONG &#x6D88;&#x606F;&#x7684;&#x65F6;&#x95F4;&#x6233; */</span>\n    <span class=\"hljs-keyword\">char</span> ip[NET_IP_STR_LEN];  <span class=\"hljs-comment\">/* IP address last time it was seen */</span>\n    <span class=\"hljs-keyword\">uint16_t</span> port;       <span class=\"hljs-comment\">/* IP*/</span>       \n    <span class=\"hljs-keyword\">uint16_t</span> cport;      <span class=\"hljs-comment\">/* &#x7AEF;&#x53E3;*/</span>  \n    <span class=\"hljs-keyword\">uint16_t</span> flags;      <span class=\"hljs-comment\">/* &#x6807;&#x8BC6;*/</span> \n    <span class=\"hljs-keyword\">uint32_t</span> notused1;   <span class=\"hljs-comment\">/* &#x5BF9;&#x9F50;&#x5B57;&#x7B26;*/</span>\n} clusterMsgDataGossip;\n\n<span class=\"hljs-keyword\">typedef</span> <span class=\"hljs-class\"><span class=\"hljs-keyword\">struct</span> {</span>\n    <span class=\"hljs-keyword\">char</span> nodename[CLUSTER_NAMELEN]; <span class=\"hljs-comment\">/* &#x4E0B;&#x7EBF;&#x8282;&#x70B9;&#x7684;&#x540D;&#x5B57; */</span>\n} clusterMsgDataFail;\n<span class=\"copy-code-btn\">&#x590D;&#x5236;&#x4EE3;&#x7801;</span></code></pre>\n<p>&#x770B;&#x5B8C;&#x4E86;&#x8282;&#x70B9;&#x7EF4;&#x62A4;&#x7684;&#x6570;&#x636E;&#x7ED3;&#x6784;&#x4F53;&#x548C;&#x53D1;&#x9001;&#x7684;&#x6D88;&#x606F;&#x7ED3;&#x6784;&#x4F53;&#x540E;&#xFF0C;&#x6211;&#x4EEC;&#x5C31;&#x6765;&#x770B;&#x770B; Redis &#x7684;&#x5177;&#x4F53;&#x884C;&#x4E3A;&#x6E90;&#x7801;&#x4E86;&#x3002;</p>\n<h4 data-id=\"heading-9\">&#x968F;&#x673A;&#x5468;&#x671F;&#x6027;&#x53D1;&#x9001;PING&#x6D88;&#x606F;</h4>\n<p>Redis &#x7684; clusterCron &#x51FD;&#x6570;&#x4F1A;&#x88AB;&#x5B9A;&#x65F6;&#x8C03;&#x7528;&#xFF0C;&#x6BCF;&#x88AB;&#x6267;&#x884C;10&#x6B21;&#xFF0C;&#x5C31;&#x4F1A;&#x51C6;&#x5907;&#x5411;&#x968F;&#x673A;&#x7684;&#x4E00;&#x4E2A;&#x8282;&#x70B9;&#x53D1;&#x9001; PING &#x6D88;&#x606F;&#x3002;</p>\n<p>&#x5B83;&#x4F1A;&#x5148;&#x968F;&#x673A;&#x7684;&#x9009;&#x51FA; 5 &#x4E2A;&#x8282;&#x70B9;&#xFF0C;&#x7136;&#x540E;&#x4ECE;&#x4E2D;&#x9009;&#x62E9;&#x6700;&#x4E45;&#x6CA1;&#x6709;&#x4E0E;&#x4E4B;&#x901A;&#x4FE1;&#x7684;&#x8282;&#x70B9;&#xFF0C;&#x8C03;&#x7528; clusterSendPing &#x51FD;&#x6570;&#x53D1;&#x9001;&#x7C7B;&#x578B;&#x4E3A; CLUSTERMSG_TYPE_PING &#x7684;&#x6D88;&#x606F;</p>\n<pre><code class=\"hljs language-cpp copyable\" lang=\"cpp\"><span class=\"hljs-comment\">// cluster.c &#x6587;&#x4EF6; </span>\n<span class=\"hljs-comment\">// clusterCron() &#x6BCF;&#x6267;&#x884C; 10 &#x6B21;&#xFF08;&#x81F3;&#x5C11;&#x95F4;&#x9694;&#x4E00;&#x79D2;&#x949F;&#xFF09;&#xFF0C;&#x5C31;&#x5411;&#x4E00;&#x4E2A;&#x968F;&#x673A;&#x8282;&#x70B9;&#x53D1;&#x9001; gossip &#x4FE1;&#x606F;</span>\n<span class=\"hljs-keyword\">if</span> (!(iteration % <span class=\"hljs-number\">10</span>)) {\n    <span class=\"hljs-keyword\">int</span> j;\n\n    <span class=\"hljs-comment\">/* &#x968F;&#x673A; 5 &#x4E2A;&#x8282;&#x70B9;&#xFF0C;&#x9009;&#x51FA;&#x5176;&#x4E2D;&#x4E00;&#x4E2A; */</span>\n    <span class=\"hljs-keyword\">for</span> (j = <span class=\"hljs-number\">0</span>; j &lt; <span class=\"hljs-number\">5</span>; j++) {\n        de = dictGetRandomKey(server.cluster-&gt;nodes);\n        clusterNode *<span class=\"hljs-keyword\">this</span> = dictGetVal(de);\n\n        <span class=\"hljs-comment\">/* &#x4E0D;&#x8981; PING &#x8FDE;&#x63A5;&#x65AD;&#x5F00;&#x7684;&#x8282;&#x70B9;&#xFF0C;&#x4E5F;&#x4E0D;&#x8981; PING &#x6700;&#x8FD1;&#x5DF2;&#x7ECF; PING &#x8FC7;&#x7684;&#x8282;&#x70B9; */</span>\n        <span class=\"hljs-keyword\">if</span> (<span class=\"hljs-keyword\">this</span>-&gt;link == <span class=\"hljs-literal\">NULL</span> || <span class=\"hljs-keyword\">this</span>-&gt;ping_sent != <span class=\"hljs-number\">0</span>) <span class=\"hljs-keyword\">continue</span>;\n        <span class=\"hljs-keyword\">if</span> (<span class=\"hljs-keyword\">this</span>-&gt;flags &amp; (CLUSTER_NODE_MYSELF|CLUSTER_NODE_HANDSHAKE))\n            <span class=\"hljs-keyword\">continue</span>;\n        <span class=\"hljs-comment\">/* &#x5BF9;&#x6BD4; pong_received &#x5B57;&#x6BB5;&#xFF0C;&#x9009;&#x51FA;&#x66F4;&#x957F;&#x65F6;&#x95F4;&#x672A;&#x6536;&#x5230;&#x5176; PONG &#x6D88;&#x606F;&#x7684;&#x8282;&#x70B9;(&#x8868;&#x793A;&#x597D;&#x4E45;&#x6CA1;&#x6709;&#x63A5;&#x53D7;&#x5230;&#x8BE5;&#x8282;&#x70B9;&#x7684;PONG&#x6D88;&#x606F;&#x4E86;) */</span>\n        <span class=\"hljs-keyword\">if</span> (min_pong_node == <span class=\"hljs-literal\">NULL</span> || min_pong &gt; <span class=\"hljs-keyword\">this</span>-&gt;pong_received) {\n            min_pong_node = <span class=\"hljs-keyword\">this</span>;\n            min_pong = <span class=\"hljs-keyword\">this</span>-&gt;pong_received;\n        }\n    }\n    <span class=\"hljs-comment\">/* &#x5411;&#x6700;&#x4E45;&#x6CA1;&#x6709;&#x6536;&#x5230; PONG &#x56DE;&#x590D;&#x7684;&#x8282;&#x70B9;&#x53D1;&#x9001; PING &#x547D;&#x4EE4; */</span>\n    <span class=\"hljs-keyword\">if</span> (min_pong_node) {\n        serverLog(LL_DEBUG,<span class=\"hljs-string\">&quot;Pinging node %.40s&quot;</span>, min_pong_node-&gt;name);\n        clusterSendPing(min_pong_node-&gt;link, CLUSTERMSG_TYPE_PING);\n    }\n}\n<span class=\"copy-code-btn\">&#x590D;&#x5236;&#x4EE3;&#x7801;</span></code></pre>\n<p>clusterSendPing &#x51FD;&#x6570;&#x7684;&#x5177;&#x4F53;&#x884C;&#x4E3A;&#x6211;&#x4EEC;&#x540E;&#x7EED;&#x518D;&#x4E86;&#x89E3;&#xFF0C;&#x56E0;&#x4E3A;&#x8BE5;&#x51FD;&#x6570;&#x5728;&#x5176;&#x4ED6;&#x73AF;&#x8282;&#x4E5F;&#x4F1A;&#x7ECF;&#x5E38;&#x7528;&#x5230;</p>\n<h4 data-id=\"heading-10\">&#x8282;&#x70B9;&#x52A0;&#x5165;&#x96C6;&#x7FA4;</h4>\n<p>&#x5F53;&#x8282;&#x70B9;&#x6267;&#x884C; CLUSTER MEET &#x547D;&#x4EE4;&#x540E;&#xFF0C;&#x4F1A;&#x5728;&#x81EA;&#x8EAB;&#x7ED9;&#x65B0;&#x8282;&#x70B9;&#x7EF4;&#x62A4;&#x4E00;&#x4E2A; clusterNode &#x7ED3;&#x6784;&#x4F53;&#xFF0C;&#x8BE5;&#x7ED3;&#x6784;&#x4F53;&#x7684; link &#x4E5F;&#x5C31;&#x662F;TCP&#x8FDE;&#x63A5;&#x5B57;&#x6BB5;&#x662F; null&#xFF0C;&#x8868;&#x793A;&#x662F;&#x65B0;&#x8282;&#x70B9;&#x5C1A;&#x672A;&#x5EFA;&#x7ACB;&#x8FDE;&#x63A5;&#x3002;</p>\n<p>clusterCron &#x51FD;&#x6570;&#x4E2D;&#x4E5F;&#x4F1A;&#x5904;&#x7406;&#x8FD9;&#x4E9B;&#x672A;&#x5EFA;&#x7ACB;&#x8FDE;&#x63A5;&#x7684;&#x65B0;&#x8282;&#x70B9;&#xFF0C;&#x8C03;&#x7528; createClusterLink &#x521B;&#x7ACB;&#x8FDE;&#x63A5;&#xFF0C;&#x7136;&#x540E;&#x8C03;&#x7528; clusterSendPing &#x51FD;&#x6570;&#x6765;&#x53D1;&#x9001; MEET &#x6D88;&#x606F;</p>\n<pre><code class=\"hljs language-cpp copyable\" lang=\"cpp\"><span class=\"hljs-comment\">/* cluster.c clusterCron &#x51FD;&#x6570;&#x90E8;&#x5206;,&#x4E3A;&#x672A;&#x521B;&#x5EFA;&#x8FDE;&#x63A5;&#x7684;&#x8282;&#x70B9;&#x521B;&#x5EFA;&#x8FDE;&#x63A5; */</span>\n<span class=\"hljs-keyword\">if</span> (node-&gt;link == <span class=\"hljs-literal\">NULL</span>) {\n    <span class=\"hljs-keyword\">int</span> fd;\n    <span class=\"hljs-keyword\">mstime_t</span> old_ping_sent;\n    clusterLink *link;\n    <span class=\"hljs-comment\">/* &#x548C;&#x8BE5;&#x8282;&#x70B9;&#x5EFA;&#x7ACB;&#x8FDE;&#x63A5; */</span>\n    fd = anetTcpNonBlockBindConnect(server.neterr, node-&gt;ip,\n        node-&gt;cport, NET_FIRST_BIND_ADDR);\n    <span class=\"hljs-comment\">/* .... fd &#x4E3A;-1&#x65F6;&#x7684;&#x5F02;&#x5E38;&#x5904;&#x7406; */</span>\n    <span class=\"hljs-comment\">/* &#x5EFA;&#x7ACB; link */</span>\n    link = createClusterLink(node);\n    link-&gt;fd = fd;\n    node-&gt;link = link;\n    aeCreateFileEvent(server.el,link-&gt;fd,AE_READABLE,\n            clusterReadHandler,link);\n    <span class=\"hljs-comment\">/* &#x5411;&#x65B0;&#x8FDE;&#x63A5;&#x7684;&#x8282;&#x70B9;&#x53D1;&#x9001; PING &#x547D;&#x4EE4;&#xFF0C;&#x9632;&#x6B62;&#x8282;&#x70B9;&#x88AB;&#x8BC6;&#x8FDB;&#x5165;&#x4E0B;&#x7EBF; */</span>\n    <span class=\"hljs-comment\">/* &#x5982;&#x679C;&#x8282;&#x70B9;&#x88AB;&#x6807;&#x8BB0;&#x4E3A; MEET &#xFF0C;&#x90A3;&#x4E48;&#x53D1;&#x9001; MEET &#x547D;&#x4EE4;&#xFF0C;&#x5426;&#x5219;&#x53D1;&#x9001; PING &#x547D;&#x4EE4; */</span>\n    old_ping_sent = node-&gt;ping_sent;\n    clusterSendPing(link, node-&gt;flags &amp; CLUSTER_NODE_MEET ?\n            CLUSTERMSG_TYPE_MEET : CLUSTERMSG_TYPE_PING);\n    <span class=\"hljs-comment\">/* .... */</span>\n    <span class=\"hljs-comment\">/* &#x5982;&#x679C;&#x5F53;&#x524D;&#x8282;&#x70B9;&#xFF08;&#x53D1;&#x9001;&#x8005;&#xFF09;&#x6CA1;&#x80FD;&#x6536;&#x5230; MEET &#x4FE1;&#x606F;&#x7684;&#x56DE;&#x590D;&#xFF0C;&#x90A3;&#x4E48;&#x5B83;&#x5C06;&#x4E0D;&#x518D;&#x5411;&#x76EE;&#x6807;&#x8282;&#x70B9;&#x53D1;&#x9001;&#x547D;&#x4EE4;&#x3002;*/</span>\n    <span class=\"hljs-comment\">/* &#x5982;&#x679C;&#x63A5;&#x6536;&#x5230;&#x56DE;&#x590D;&#x7684;&#x8BDD;&#xFF0C;&#x90A3;&#x4E48;&#x8282;&#x70B9;&#x5C06;&#x4E0D;&#x518D;&#x5904;&#x4E8E; HANDSHAKE &#x72B6;&#x6001;&#xFF0C;&#x5E76;&#x7EE7;&#x7EED;&#x5411;&#x76EE;&#x6807;&#x8282;&#x70B9;&#x53D1;&#x9001;&#x666E;&#x901A; PING &#x547D;&#x4EE4;*/</span>\n    node-&gt;flags &amp;= ~CLUSTER_NODE_MEET;\n}\n<span class=\"copy-code-btn\">&#x590D;&#x5236;&#x4EE3;&#x7801;</span></code></pre>\n<h4 data-id=\"heading-11\">&#x9632;&#x6B62;&#x8282;&#x70B9;&#x5047;&#x8D85;&#x65F6;&#x53CA;&#x72B6;&#x6001;&#x8FC7;&#x671F;</h4>\n<p>&#x9632;&#x6B62;&#x8282;&#x70B9;&#x5047;&#x8D85;&#x65F6;&#x548C;&#x6807;&#x8BB0;&#x7591;&#x4F3C;&#x4E0B;&#x7EBF;&#x6807;&#x8BB0;&#x4E5F;&#x662F;&#x5728; clusterCron &#x51FD;&#x6570;&#x4E2D;&#xFF0C;&#x5177;&#x4F53;&#x5982;&#x4E0B;&#x6240;&#x793A;&#x3002;&#x5B83;&#x4F1A;&#x68C0;&#x67E5;&#x5F53;&#x524D;&#x6240;&#x6709;&#x7684; nodes &#x8282;&#x70B9;&#x5217;&#x8868;&#xFF0C;&#x5982;&#x679C;&#x53D1;&#x73B0;&#x67D0;&#x4E2A;&#x8282;&#x70B9;&#x4E0E;&#x81EA;&#x5DF1;&#x7684;&#x6700;&#x540E;&#x4E00;&#x4E2A; PONG &#x901A;&#x4FE1;&#x65F6;&#x95F4;&#x8D85;&#x8FC7;&#x4E86;&#x9884;&#x5B9A;&#x7684;&#x9608;&#x503C;&#x7684;&#x4E00;&#x534A;&#x65F6;&#xFF0C;&#x4E3A;&#x4E86;&#x9632;&#x6B62;&#x8282;&#x70B9;&#x662F;&#x5047;&#x8D85;&#x65F6;&#xFF0C;&#x4F1A;&#x4E3B;&#x52A8;&#x91CA;&#x653E;&#x6389;&#x4E0E;&#x4E4B;&#x7684; link &#x8FDE;&#x63A5;&#xFF0C;&#x7136;&#x540E;&#x4F1A;&#x4E3B;&#x52A8;&#x5411;&#x5B83;&#x53D1;&#x9001;&#x4E00;&#x4E2A; PING &#x6D88;&#x606F;&#x3002;</p>\n<pre><code class=\"hljs language-cpp copyable\" lang=\"cpp\"><span class=\"hljs-comment\">/* cluster.c clusterCron &#x51FD;&#x6570;&#x90E8;&#x5206;&#xFF0C;&#x904D;&#x5386;&#x8282;&#x70B9;&#x6765;&#x68C0;&#x67E5; fail &#x7684;&#x8282;&#x70B9;*/</span>\n<span class=\"hljs-keyword\">while</span>((de = dictNext(di)) != <span class=\"hljs-literal\">NULL</span>) {\n    clusterNode *node = dictGetVal(de);\n    now = mstime(); <span class=\"hljs-comment\">/* Use an updated time at every iteration. */</span>\n    <span class=\"hljs-keyword\">mstime_t</span> delay;\n\n    <span class=\"hljs-comment\">/* &#x5982;&#x679C;&#x7B49;&#x5230; PONG &#x5230;&#x8FBE;&#x7684;&#x65F6;&#x95F4;&#x8D85;&#x8FC7;&#x4E86; node timeout &#x4E00;&#x534A;&#x7684;&#x8FDE;&#x63A5; */</span>\n    <span class=\"hljs-comment\">/* &#x56E0;&#x4E3A;&#x5C3D;&#x7BA1;&#x8282;&#x70B9;&#x4F9D;&#x7136;&#x6B63;&#x5E38;&#xFF0C;&#x4F46;&#x8FDE;&#x63A5;&#x53EF;&#x80FD;&#x5DF2;&#x7ECF;&#x51FA;&#x95EE;&#x9898;&#x4E86; */</span>\n    <span class=\"hljs-keyword\">if</span> (node-&gt;link &amp;&amp; <span class=\"hljs-comment\">/* is connected */</span>\n        now - node-&gt;link-&gt;ctime &gt;\n        server.cluster_node_timeout &amp;&amp; <span class=\"hljs-comment\">/* &#x8FD8;&#x672A;&#x91CD;&#x8FDE; */</span>\n        node-&gt;ping_sent &amp;&amp; <span class=\"hljs-comment\">/* &#x5DF2;&#x7ECF;&#x53D1;&#x8FC7;ping&#x6D88;&#x606F; */</span>\n        node-&gt;pong_received &lt; node-&gt;ping_sent &amp;&amp; <span class=\"hljs-comment\">/* &#x8FD8;&#x5728;&#x7B49;&#x5F85;pong&#x6D88;&#x606F; */</span>\n        <span class=\"hljs-comment\">/* &#x7B49;&#x5F85;pong&#x6D88;&#x606F;&#x8D85;&#x8FC7;&#x4E86; timeout/2 */</span>\n        now - node-&gt;ping_sent &gt; server.cluster_node_timeout/<span class=\"hljs-number\">2</span>)\n    {\n        <span class=\"hljs-comment\">/* &#x91CA;&#x653E;&#x8FDE;&#x63A5;&#xFF0C;&#x4E0B;&#x6B21; clusterCron() &#x4F1A;&#x81EA;&#x52A8;&#x91CD;&#x8FDE; */</span>\n        freeClusterLink(node-&gt;link);\n    }\n\n    <span class=\"hljs-comment\">/* &#x5982;&#x679C;&#x76EE;&#x524D;&#x6CA1;&#x6709;&#x5728; PING &#x8282;&#x70B9;*/</span>\n    <span class=\"hljs-comment\">/* &#x5E76;&#x4E14;&#x5DF2;&#x7ECF;&#x6709; node timeout &#x4E00;&#x534A;&#x7684;&#x65F6;&#x95F4;&#x6CA1;&#x6709;&#x4ECE;&#x8282;&#x70B9;&#x90A3;&#x91CC;&#x6536;&#x5230; PONG &#x56DE;&#x590D; */</span>\n    <span class=\"hljs-comment\">/* &#x90A3;&#x4E48;&#x5411;&#x8282;&#x70B9;&#x53D1;&#x9001;&#x4E00;&#x4E2A; PING &#xFF0C;&#x786E;&#x4FDD;&#x8282;&#x70B9;&#x7684;&#x4FE1;&#x606F;&#x4E0D;&#x4F1A;&#x592A;&#x65E7;&#xFF0C;&#x6709;&#x53EF;&#x80FD;&#x4E00;&#x76F4;&#x6CA1;&#x6709;&#x968F;&#x673A;&#x4E2D; */</span>\n    <span class=\"hljs-keyword\">if</span> (node-&gt;link &amp;&amp;\n        node-&gt;ping_sent == <span class=\"hljs-number\">0</span> &amp;&amp;\n        (now - node-&gt;pong_received) &gt; server.cluster_node_timeout/<span class=\"hljs-number\">2</span>)\n    {\n        clusterSendPing(node-&gt;link, CLUSTERMSG_TYPE_PING);\n        <span class=\"hljs-keyword\">continue</span>;\n    }\n    <span class=\"hljs-comment\">/* .... &#x5904;&#x7406;failover&#x548C;&#x6807;&#x8BB0;&#x9057;&#x5931;&#x4E0B;&#x7EBF; */</span>\n}\n<span class=\"copy-code-btn\">&#x590D;&#x5236;&#x4EE3;&#x7801;</span></code></pre>\n<h4 data-id=\"heading-12\">&#x5904;&#x7406;failover&#x548C;&#x6807;&#x8BB0;&#x7591;&#x4F3C;&#x4E0B;&#x7EBF;</h4>\n<p>&#x5982;&#x679C;&#x9632;&#x6B62;&#x8282;&#x70B9;&#x5047;&#x8D85;&#x65F6;&#x5904;&#x7406;&#x540E;&#xFF0C;&#x8282;&#x70B9;&#x4F9D;&#x65E7;&#x672A;&#x6536;&#x5230;&#x76EE;&#x6807;&#x8282;&#x70B9;&#x7684; PONG &#x6D88;&#x606F;&#xFF0C;&#x5E76;&#x4E14;&#x65F6;&#x95F4;&#x5DF2;&#x7ECF;&#x8D85;&#x8FC7;&#x4E86; cluster_node_timeout&#xFF0C;&#x90A3;&#x4E48;&#x5C31;&#x5C06;&#x8BE5;&#x8282;&#x70B9;&#x6807;&#x8BB0;&#x4E3A;&#x7591;&#x4F3C;&#x4E0B;&#x7EBF;&#x72B6;&#x6001;&#x3002;</p>\n<pre><code class=\"hljs language-cpp copyable\" lang=\"cpp\"><span class=\"hljs-comment\">/* &#x5982;&#x679C;&#x8FD9;&#x662F;&#x4E00;&#x4E2A;&#x4E3B;&#x8282;&#x70B9;&#xFF0C;&#x5E76;&#x4E14;&#x6709;&#x4E00;&#x4E2A;&#x4ECE;&#x670D;&#x52A1;&#x5668;&#x8BF7;&#x6C42;&#x8FDB;&#x884C;&#x624B;&#x52A8;&#x6545;&#x969C;&#x8F6C;&#x79FB;,&#x90A3;&#x4E48;&#x5411;&#x4ECE;&#x670D;&#x52A1;&#x5668;&#x53D1;&#x9001; PING*/</span>\n<span class=\"hljs-keyword\">if</span> (server.cluster-&gt;mf_end &amp;&amp;\n    nodeIsMaster(myself) &amp;&amp;\n    server.cluster-&gt;mf_slave == node &amp;&amp;\n    node-&gt;link)\n{\n    clusterSendPing(node-&gt;link, CLUSTERMSG_TYPE_PING);\n    <span class=\"hljs-keyword\">continue</span>;\n}\n\n<span class=\"hljs-comment\">/* &#x540E;&#x7EED;&#x4EE3;&#x7801;&#x53EA;&#x5728;&#x8282;&#x70B9;&#x53D1;&#x9001;&#x4E86; PING &#x547D;&#x4EE4;&#x7684;&#x60C5;&#x51B5;&#x4E0B;&#x6267;&#x884C;*/</span>\n<span class=\"hljs-keyword\">if</span> (node-&gt;ping_sent == <span class=\"hljs-number\">0</span>) <span class=\"hljs-keyword\">continue</span>;\n\n<span class=\"hljs-comment\">/* &#x8BA1;&#x7B97;&#x7B49;&#x5F85; PONG &#x56DE;&#x590D;&#x7684;&#x65F6;&#x957F; */</span> \ndelay = now - node-&gt;ping_sent;\n<span class=\"hljs-comment\">/* &#x7B49;&#x5F85; PONG &#x56DE;&#x590D;&#x7684;&#x65F6;&#x957F;&#x8D85;&#x8FC7;&#x4E86;&#x9650;&#x5236;&#x503C;&#xFF0C;&#x5C06;&#x76EE;&#x6807;&#x8282;&#x70B9;&#x6807;&#x8BB0;&#x4E3A; PFAIL &#xFF08;&#x7591;&#x4F3C;&#x4E0B;&#x7EBF;)*/</span>\n<span class=\"hljs-keyword\">if</span> (delay &gt; server.cluster_node_timeout) {\n    <span class=\"hljs-comment\">/* &#x8D85;&#x65F6;&#x4E86;&#xFF0C;&#x6807;&#x8BB0;&#x4E3A;&#x7591;&#x4F3C;&#x4E0B;&#x7EBF; */</span>\n    <span class=\"hljs-keyword\">if</span> (!(node-&gt;flags &amp; (REDIS_NODE_PFAIL|REDIS_NODE_FAIL))) {\n        redisLog(REDIS_DEBUG,<span class=\"hljs-string\">&quot;*** NODE %.40s possibly failing&quot;</span>,\n            node-&gt;name);\n        <span class=\"hljs-comment\">// &#x6253;&#x5F00;&#x7591;&#x4F3C;&#x4E0B;&#x7EBF;&#x6807;&#x8BB0;</span>\n        node-&gt;flags |= REDIS_NODE_PFAIL;\n        update_state = <span class=\"hljs-number\">1</span>;\n    }\n}\n<span class=\"copy-code-btn\">&#x590D;&#x5236;&#x4EE3;&#x7801;</span></code></pre>\n<h4 data-id=\"heading-13\">&#x5B9E;&#x9645;&#x53D1;&#x9001;Gossip&#x6D88;&#x606F;</h4>\n<p>&#x4EE5;&#x4E0B;&#x662F;&#x524D;&#x65B9;&#x591A;&#x6B21;&#x8C03;&#x7528;&#x8FC7;&#x7684;clusterSendPing()&#x65B9;&#x6CD5;&#x7684;&#x6E90;&#x7801;&#xFF0C;&#x4EE3;&#x7801;&#x4E2D;&#x6709;&#x8BE6;&#x7EC6;&#x7684;&#x6CE8;&#x91CA;&#xFF0C;&#x5927;&#x5BB6;&#x53EF;&#x4EE5;&#x81EA;&#x884C;&#x9605;&#x8BFB;&#x3002;&#x4E3B;&#x8981;&#x7684;&#x64CD;&#x4F5C;&#x5C31;&#x662F;&#x5C06;&#x8282;&#x70B9;&#x81EA;&#x8EAB;&#x7EF4;&#x62A4;&#x7684; clusterState &#x8F6C;&#x6362;&#x4E3A;&#x5BF9;&#x5E94;&#x7684;&#x6D88;&#x606F;&#x7ED3;&#x6784;&#x4F53;&#xFF0C;&#x3002;</p>\n<pre><code class=\"hljs language-cpp copyable\" lang=\"cpp\"><span class=\"hljs-comment\">/* &#x5411;&#x6307;&#x5B9A;&#x8282;&#x70B9;&#x53D1;&#x9001;&#x4E00;&#x6761; MEET &#x3001; PING &#x6216;&#x8005; PONG &#x6D88;&#x606F; */</span>\n<span class=\"hljs-function\"><span class=\"hljs-keyword\">void</span> <span class=\"hljs-title\">clusterSendPing</span><span class=\"hljs-params\">(clusterLink *link, <span class=\"hljs-keyword\">int</span> type)</span> </span>{\n    <span class=\"hljs-keyword\">unsigned</span> <span class=\"hljs-keyword\">char</span> *buf;\n    clusterMsg *hdr;\n    <span class=\"hljs-keyword\">int</span> gossipcount = <span class=\"hljs-number\">0</span>; <span class=\"hljs-comment\">/* Number of gossip sections added so far. */</span>\n    <span class=\"hljs-keyword\">int</span> wanted; <span class=\"hljs-comment\">/* Number of gossip sections we want to append if possible. */</span>\n    <span class=\"hljs-keyword\">int</span> totlen; <span class=\"hljs-comment\">/* Total packet length. */</span>\n    <span class=\"hljs-comment\">// freshnodes &#x662F;&#x7528;&#x4E8E;&#x53D1;&#x9001; gossip &#x4FE1;&#x606F;&#x7684;&#x8BA1;&#x6570;&#x5668;</span>\n    <span class=\"hljs-comment\">// &#x6BCF;&#x6B21;&#x53D1;&#x9001;&#x4E00;&#x6761;&#x4FE1;&#x606F;&#x65F6;&#xFF0C;&#x7A0B;&#x5E8F;&#x5C06; freshnodes &#x7684;&#x503C;&#x51CF;&#x4E00;</span>\n    <span class=\"hljs-comment\">// &#x5F53; freshnodes &#x7684;&#x6570;&#x503C;&#x5C0F;&#x4E8E;&#x7B49;&#x4E8E; 0 &#x65F6;&#xFF0C;&#x7A0B;&#x5E8F;&#x505C;&#x6B62;&#x53D1;&#x9001; gossip &#x4FE1;&#x606F;</span>\n    <span class=\"hljs-comment\">// freshnodes &#x7684;&#x6570;&#x91CF;&#x662F;&#x8282;&#x70B9;&#x76EE;&#x524D;&#x7684; nodes &#x8868;&#x4E2D;&#x7684;&#x8282;&#x70B9;&#x6570;&#x91CF;&#x51CF;&#x53BB; 2 </span>\n    <span class=\"hljs-comment\">// &#x8FD9;&#x91CC;&#x7684; 2 &#x6307;&#x4E24;&#x4E2A;&#x8282;&#x70B9;&#xFF0C;&#x4E00;&#x4E2A;&#x662F; myself &#x8282;&#x70B9;&#xFF08;&#x4E5F;&#x5373;&#x662F;&#x53D1;&#x9001;&#x4FE1;&#x606F;&#x7684;&#x8FD9;&#x4E2A;&#x8282;&#x70B9;&#xFF09;</span>\n    <span class=\"hljs-comment\">// &#x53E6;&#x4E00;&#x4E2A;&#x662F;&#x63A5;&#x53D7; gossip &#x4FE1;&#x606F;&#x7684;&#x8282;&#x70B9;</span>\n    <span class=\"hljs-keyword\">int</span> freshnodes = dictSize(server.cluster-&gt;nodes)<span class=\"hljs-number\">-2</span>;\n\n    \n    <span class=\"hljs-comment\">/* &#x8BA1;&#x7B97;&#x8981;&#x643A;&#x5E26;&#x591A;&#x5C11;&#x8282;&#x70B9;&#x7684;&#x4FE1;&#x606F;&#xFF0C;&#x6700;&#x5C11;3&#x4E2A;&#xFF0C;&#x6700;&#x591A; 1/10 &#x96C6;&#x7FA4;&#x603B;&#x8282;&#x70B9;&#x6570;&#x91CF;*/</span>\n    wanted = <span class=\"hljs-built_in\">floor</span>(dictSize(server.cluster-&gt;nodes)/<span class=\"hljs-number\">10</span>);\n    <span class=\"hljs-keyword\">if</span> (wanted &lt; <span class=\"hljs-number\">3</span>) wanted = <span class=\"hljs-number\">3</span>;\n    <span class=\"hljs-keyword\">if</span> (wanted &gt; freshnodes) wanted = freshnodes;\n\n    <span class=\"hljs-comment\">/* .... &#x7701;&#x7565; totlen &#x7684;&#x8BA1;&#x7B97;&#x7B49;*/</span>\n\n    <span class=\"hljs-comment\">/* &#x5982;&#x679C;&#x53D1;&#x9001;&#x7684;&#x4FE1;&#x606F;&#x662F; PING &#xFF0C;&#x90A3;&#x4E48;&#x66F4;&#x65B0;&#x6700;&#x540E;&#x4E00;&#x6B21;&#x53D1;&#x9001; PING &#x547D;&#x4EE4;&#x7684;&#x65F6;&#x95F4;&#x6233; */</span>\n    <span class=\"hljs-keyword\">if</span> (link-&gt;node &amp;&amp; type == CLUSTERMSG_TYPE_PING)\n        link-&gt;node-&gt;ping_sent = mstime();\n    <span class=\"hljs-comment\">/* &#x5C06;&#x5F53;&#x524D;&#x8282;&#x70B9;&#x7684;&#x4FE1;&#x606F;&#xFF08;&#x6BD4;&#x5982;&#x540D;&#x5B57;&#x3001;&#x5730;&#x5740;&#x3001;&#x7AEF;&#x53E3;&#x53F7;&#x3001;&#x8D1F;&#x8D23;&#x5904;&#x7406;&#x7684;&#x69FD;&#xFF09;&#x8BB0;&#x5F55;&#x5230;&#x6D88;&#x606F;&#x91CC;&#x9762; */</span>\n    clusterBuildMessageHdr(hdr,type);\n\n    <span class=\"hljs-comment\">/* Populate the gossip fields */</span>\n    <span class=\"hljs-keyword\">int</span> maxiterations = wanted*<span class=\"hljs-number\">3</span>;\n    <span class=\"hljs-comment\">/* &#x6BCF;&#x4E2A;&#x8282;&#x70B9;&#x6709; freshnodes &#x6B21;&#x53D1;&#x9001; gossip &#x4FE1;&#x606F;&#x7684;&#x673A;&#x4F1A;\n       &#x6BCF;&#x6B21;&#x5411;&#x76EE;&#x6807;&#x8282;&#x70B9;&#x53D1;&#x9001; 2 &#x4E2A;&#x88AB;&#x9009;&#x4E2D;&#x8282;&#x70B9;&#x7684; gossip &#x4FE1;&#x606F;&#xFF08;gossipcount &#x8BA1;&#x6570;&#xFF09; */</span>\n    <span class=\"hljs-keyword\">while</span>(freshnodes &gt; <span class=\"hljs-number\">0</span> &amp;&amp; gossipcount &lt; wanted &amp;&amp; maxiterations--) {\n        <span class=\"hljs-comment\">/* &#x4ECE; nodes &#x5B57;&#x5178;&#x4E2D;&#x968F;&#x673A;&#x9009;&#x51FA;&#x4E00;&#x4E2A;&#x8282;&#x70B9;&#xFF08;&#x88AB;&#x9009;&#x4E2D;&#x8282;&#x70B9;&#xFF09; */</span>\n        dictEntry *de = dictGetRandomKey(server.cluster-&gt;nodes);\n        clusterNode *<span class=\"hljs-keyword\">this</span> = dictGetVal(de);\n\n        <span class=\"hljs-comment\">/* &#x4EE5;&#x4E0B;&#x8282;&#x70B9;&#x4E0D;&#x80FD;&#x4F5C;&#x4E3A;&#x88AB;&#x9009;&#x4E2D;&#x8282;&#x70B9;&#xFF1A;\n         * Myself:&#x8282;&#x70B9;&#x672C;&#x8EAB;&#x3002;\n         * PFAIL&#x72B6;&#x6001;&#x7684;&#x8282;&#x70B9;\n         * &#x5904;&#x4E8E; HANDSHAKE &#x72B6;&#x6001;&#x7684;&#x8282;&#x70B9;&#x3002;\n         * &#x5E26;&#x6709; NOADDR &#x6807;&#x8BC6;&#x7684;&#x8282;&#x70B9;\n         * &#x56E0;&#x4E3A;&#x4E0D;&#x5904;&#x7406;&#x4EFB;&#x4F55; Slot &#x800C;&#x88AB;&#x65AD;&#x5F00;&#x8FDE;&#x63A5;&#x7684;&#x8282;&#x70B9; \n         */</span>\n        <span class=\"hljs-keyword\">if</span> (<span class=\"hljs-keyword\">this</span> == myself) <span class=\"hljs-keyword\">continue</span>;\n        <span class=\"hljs-keyword\">if</span> (<span class=\"hljs-keyword\">this</span>-&gt;flags &amp; CLUSTER_NODE_PFAIL) <span class=\"hljs-keyword\">continue</span>;\n        <span class=\"hljs-keyword\">if</span> (<span class=\"hljs-keyword\">this</span>-&gt;flags &amp; (CLUSTER_NODE_HANDSHAKE|CLUSTER_NODE_NOADDR) ||\n            (<span class=\"hljs-keyword\">this</span>-&gt;link == <span class=\"hljs-literal\">NULL</span> &amp;&amp; <span class=\"hljs-keyword\">this</span>-&gt;numslots == <span class=\"hljs-number\">0</span>))\n        {\n            freshnodes--; <span class=\"hljs-comment\">/* Tecnically not correct, but saves CPU. */</span>\n            <span class=\"hljs-keyword\">continue</span>;\n        }\n\n        <span class=\"hljs-comment\">// &#x68C0;&#x67E5;&#x88AB;&#x9009;&#x4E2D;&#x8282;&#x70B9;&#x662F;&#x5426;&#x5DF2;&#x7ECF;&#x5728; hdr-&gt;data.ping.gossip &#x6570;&#x7EC4;&#x91CC;&#x9762;</span>\n        <span class=\"hljs-comment\">// &#x5982;&#x679C;&#x662F;&#x7684;&#x8BDD;&#x8BF4;&#x660E;&#x8FD9;&#x4E2A;&#x8282;&#x70B9;&#x4E4B;&#x524D;&#x5DF2;&#x7ECF;&#x88AB;&#x9009;&#x4E2D;&#x4E86;</span>\n        <span class=\"hljs-comment\">// &#x4E0D;&#x8981;&#x518D;&#x9009;&#x4E2D;&#x5B83;&#xFF08;&#x5426;&#x5219;&#x5C31;&#x4F1A;&#x51FA;&#x73B0;&#x91CD;&#x590D;&#xFF09;</span>\n        <span class=\"hljs-keyword\">if</span> (clusterNodeIsInGossipSection(hdr,gossipcount,<span class=\"hljs-keyword\">this</span>)) <span class=\"hljs-keyword\">continue</span>;\n\n        <span class=\"hljs-comment\">/* &#x8FD9;&#x4E2A;&#x88AB;&#x9009;&#x4E2D;&#x8282;&#x70B9;&#x6709;&#x6548;&#xFF0C;&#x8BA1;&#x6570;&#x5668;&#x51CF;&#x4E00; */</span>\n        clusterSetGossipEntry(hdr,gossipcount,<span class=\"hljs-keyword\">this</span>);\n        freshnodes--;\n        gossipcount++;\n    }\n\n    <span class=\"hljs-comment\">/* .... &#x5982;&#x679C;&#x6709; PFAIL &#x8282;&#x70B9;&#xFF0C;&#x6700;&#x540E;&#x6DFB;&#x52A0; */</span>\n\n\n    <span class=\"hljs-comment\">/* &#x8BA1;&#x7B97;&#x4FE1;&#x606F;&#x957F;&#x5EA6; */</span>\n    totlen = <span class=\"hljs-keyword\">sizeof</span>(clusterMsg)-<span class=\"hljs-keyword\">sizeof</span>(<span class=\"hljs-keyword\">union</span> clusterMsgData);\n    totlen += (<span class=\"hljs-keyword\">sizeof</span>(clusterMsgDataGossip)*gossipcount);\n    <span class=\"hljs-comment\">/* &#x5C06;&#x88AB;&#x9009;&#x4E2D;&#x8282;&#x70B9;&#x7684;&#x6570;&#x91CF;&#xFF08;gossip &#x4FE1;&#x606F;&#x4E2D;&#x5305;&#x542B;&#x4E86;&#x591A;&#x5C11;&#x4E2A;&#x8282;&#x70B9;&#x7684;&#x4FE1;&#x606F;&#xFF09;&#x8BB0;&#x5F55;&#x5728; count &#x5C5E;&#x6027;&#x91CC;&#x9762;*/</span>\n    hdr-&gt;count = htons(gossipcount);\n    <span class=\"hljs-comment\">/* &#x5C06;&#x4FE1;&#x606F;&#x7684;&#x957F;&#x5EA6;&#x8BB0;&#x5F55;&#x5230;&#x4FE1;&#x606F;&#x91CC;&#x9762; */</span>\n    hdr-&gt;totlen = htonl(totlen);\n    <span class=\"hljs-comment\">/* &#x53D1;&#x9001;&#x7F51;&#x7EDC;&#x8BF7;&#x6C42; */</span>\n    clusterSendMessage(link,buf,totlen);\n    zfree(buf);\n}\n\n\n<span class=\"hljs-function\"><span class=\"hljs-keyword\">void</span> <span class=\"hljs-title\">clusterSetGossipEntry</span><span class=\"hljs-params\">(clusterMsg *hdr, <span class=\"hljs-keyword\">int</span> i, clusterNode *n)</span> </span>{\n    clusterMsgDataGossip *gossip;\n    <span class=\"hljs-comment\">/* &#x6307;&#x5411; gossip &#x4FE1;&#x606F;&#x7ED3;&#x6784; */</span>\n    gossip = &amp;(hdr-&gt;data.ping.gossip[i]);\n    <span class=\"hljs-comment\">/* &#x5C06;&#x88AB;&#x9009;&#x4E2D;&#x8282;&#x70B9;&#x7684;&#x540D;&#x5B57;&#x8BB0;&#x5F55;&#x5230; gossip &#x4FE1;&#x606F; */</span>   \n    <span class=\"hljs-built_in\">memcpy</span>(gossip-&gt;nodename,n-&gt;name,CLUSTER_NAMELEN);\n    <span class=\"hljs-comment\">/* &#x5C06;&#x88AB;&#x9009;&#x4E2D;&#x8282;&#x70B9;&#x7684; PING &#x547D;&#x4EE4;&#x53D1;&#x9001;&#x65F6;&#x95F4;&#x6233;&#x8BB0;&#x5F55;&#x5230; gossip &#x4FE1;&#x606F; */</span>\n    gossip-&gt;ping_sent = htonl(n-&gt;ping_sent/<span class=\"hljs-number\">1000</span>);\n    <span class=\"hljs-comment\">/* &#x5C06;&#x88AB;&#x9009;&#x4E2D;&#x8282;&#x70B9;&#x7684; PONG &#x547D;&#x4EE4;&#x56DE;&#x590D;&#x7684;&#x65F6;&#x95F4;&#x6233;&#x8BB0;&#x5F55;&#x5230; gossip &#x4FE1;&#x606F; */</span>\n    gossip-&gt;pong_received = htonl(n-&gt;pong_received/<span class=\"hljs-number\">1000</span>);\n    <span class=\"hljs-comment\">/* &#x5C06;&#x88AB;&#x9009;&#x4E2D;&#x8282;&#x70B9;&#x7684; IP &#x8BB0;&#x5F55;&#x5230; gossip &#x4FE1;&#x606F; */</span>\n    <span class=\"hljs-built_in\">memcpy</span>(gossip-&gt;ip,n-&gt;ip,<span class=\"hljs-keyword\">sizeof</span>(n-&gt;ip));\n    <span class=\"hljs-comment\">/* &#x5C06;&#x88AB;&#x9009;&#x4E2D;&#x8282;&#x70B9;&#x7684;&#x7AEF;&#x53E3;&#x53F7;&#x8BB0;&#x5F55;&#x5230; gossip &#x4FE1;&#x606F; */</span>\n    gossip-&gt;port = htons(n-&gt;port);\n    gossip-&gt;cport = htons(n-&gt;cport);\n    <span class=\"hljs-comment\">/* &#x5C06;&#x88AB;&#x9009;&#x4E2D;&#x8282;&#x70B9;&#x7684;&#x6807;&#x8BC6;&#x503C;&#x8BB0;&#x5F55;&#x5230; gossip &#x4FE1;&#x606F; */</span>\n    gossip-&gt;flags = htons(n-&gt;flags);\n    gossip-&gt;notused1 = <span class=\"hljs-number\">0</span>;\n}\n<span class=\"copy-code-btn\">&#x590D;&#x5236;&#x4EE3;&#x7801;</span></code></pre>\n<p>&#x4E0B;&#x9762;&#x662F; clusterBuildMessageHdr &#x51FD;&#x6570;&#xFF0C;&#x5B83;&#x4E3B;&#x8981;&#x8D1F;&#x8D23;&#x586B;&#x5145;&#x6D88;&#x606F;&#x7ED3;&#x6784;&#x4F53;&#x4E2D;&#x7684;&#x57FA;&#x7840;&#x4FE1;&#x606F;&#x548C;&#x5F53;&#x524D;&#x8282;&#x70B9;&#x7684;&#x72B6;&#x6001;&#x4FE1;&#x606F;&#x3002;</p>\n<pre><code class=\"hljs language-c copyable\" lang=\"c\"><span class=\"hljs-comment\">/* &#x6784;&#x5EFA;&#x6D88;&#x606F;&#x7684; header */</span>\n<span class=\"hljs-function\"><span class=\"hljs-keyword\">void</span> <span class=\"hljs-title\">clusterBuildMessageHdr</span><span class=\"hljs-params\">(clusterMsg *hdr, <span class=\"hljs-keyword\">int</span> type)</span> </span>{\n    <span class=\"hljs-keyword\">int</span> totlen = <span class=\"hljs-number\">0</span>;\n    <span class=\"hljs-keyword\">uint64_t</span> offset;\n    clusterNode *master;\n\n    <span class=\"hljs-comment\">/* &#x5982;&#x679C;&#x5F53;&#x524D;&#x8282;&#x70B9;&#x662F;salve&#xFF0C;&#x5219;master&#x4E3A;&#x5176;&#x4E3B;&#x8282;&#x70B9;&#xFF0C;&#x5982;&#x679C;&#x5F53;&#x524D;&#x8282;&#x70B9;&#x662F;master&#x8282;&#x70B9;&#xFF0C;&#x5219;master&#x5C31;&#x662F;&#x5F53;&#x524D;&#x8282;&#x70B9; */</span>\n    master = (nodeIsSlave(myself) &amp;&amp; myself-&gt;slaveof) ?\n              myself-&gt;slaveof : myself;\n\n    <span class=\"hljs-built_in\">memset</span>(hdr,<span class=\"hljs-number\">0</span>,<span class=\"hljs-keyword\">sizeof</span>(*hdr));\n    <span class=\"hljs-comment\">/* &#x521D;&#x59CB;&#x5316;&#x534F;&#x8BAE;&#x7248;&#x672C;&#x3001;&#x6807;&#x8BC6;&#x3001;&#x53CA;&#x7C7B;&#x578B;&#xFF0C; */</span>\n    hdr-&gt;ver = htons(CLUSTER_PROTO_VER);\n    hdr-&gt;sig[<span class=\"hljs-number\">0</span>] = <span class=\"hljs-string\">&apos;R&apos;</span>;\n    hdr-&gt;sig[<span class=\"hljs-number\">1</span>] = <span class=\"hljs-string\">&apos;C&apos;</span>;\n    hdr-&gt;sig[<span class=\"hljs-number\">2</span>] = <span class=\"hljs-string\">&apos;m&apos;</span>;\n    hdr-&gt;sig[<span class=\"hljs-number\">3</span>] = <span class=\"hljs-string\">&apos;b&apos;</span>;\n    hdr-&gt;type = htons(type);\n    <span class=\"hljs-comment\">/* &#x6D88;&#x606F;&#x5934;&#x8BBE;&#x7F6E;&#x5F53;&#x524D;&#x8282;&#x70B9;id */</span>\n    <span class=\"hljs-built_in\">memcpy</span>(hdr-&gt;sender,myself-&gt;name,CLUSTER_NAMELEN);\n\n    <span class=\"hljs-comment\">/* &#x6D88;&#x606F;&#x5934;&#x8BBE;&#x7F6E;&#x5F53;&#x524D;&#x8282;&#x70B9;ip */</span>\n    <span class=\"hljs-built_in\">memset</span>(hdr-&gt;myip,<span class=\"hljs-number\">0</span>,NET_IP_STR_LEN);\n    <span class=\"hljs-keyword\">if</span> (server.cluster_announce_ip) {\n        <span class=\"hljs-built_in\">strncpy</span>(hdr-&gt;myip,server.cluster_announce_ip,NET_IP_STR_LEN);\n        hdr-&gt;myip[NET_IP_STR_LEN<span class=\"hljs-number\">-1</span>] = <span class=\"hljs-string\">&apos;\\0&apos;</span>;\n    }\n\n    <span class=\"hljs-comment\">/* &#x57FA;&#x7840;&#x7AEF;&#x53E3;&#x53CA;&#x96C6;&#x7FA4;&#x5185;&#x8282;&#x70B9;&#x901A;&#x4FE1;&#x7AEF;&#x53E3; */</span>\n    <span class=\"hljs-keyword\">int</span> announced_port = server.cluster_announce_port ?\n                         server.cluster_announce_port : server.port;\n    <span class=\"hljs-keyword\">int</span> announced_cport = server.cluster_announce_bus_port ?\n                          server.cluster_announce_bus_port :\n                          (server.port + CLUSTER_PORT_INCR);\n    <span class=\"hljs-comment\">/* &#x8BBE;&#x7F6E;&#x5F53;&#x524D;&#x8282;&#x70B9;&#x7684;&#x69FD;&#x4FE1;&#x606F; */</span>\n    <span class=\"hljs-built_in\">memcpy</span>(hdr-&gt;myslots,master-&gt;slots,<span class=\"hljs-keyword\">sizeof</span>(hdr-&gt;myslots));\n    <span class=\"hljs-built_in\">memset</span>(hdr-&gt;slaveof,<span class=\"hljs-number\">0</span>,CLUSTER_NAMELEN);\n    <span class=\"hljs-keyword\">if</span> (myself-&gt;slaveof != <span class=\"hljs-literal\">NULL</span>)\n        <span class=\"hljs-built_in\">memcpy</span>(hdr-&gt;slaveof,myself-&gt;slaveof-&gt;name, CLUSTER_NAMELEN);\n    hdr-&gt;port = htons(announced_port);\n    hdr-&gt;cport = htons(announced_cport);\n    hdr-&gt;flags = htons(myself-&gt;flags);\n    hdr-&gt;state = server.cluster-&gt;state;\n\n    <span class=\"hljs-comment\">/* &#x8BBE;&#x7F6E; currentEpoch and configEpochs. */</span>\n    hdr-&gt;currentEpoch = htonu64(server.cluster-&gt;currentEpoch);\n    hdr-&gt;configEpoch = htonu64(master-&gt;configEpoch);\n\n    <span class=\"hljs-comment\">/* &#x8BBE;&#x7F6E;&#x590D;&#x5236;&#x504F;&#x79FB;&#x91CF; */</span>\n    <span class=\"hljs-keyword\">if</span> (nodeIsSlave(myself))\n        offset = replicationGetSlaveOffset();\n    <span class=\"hljs-keyword\">else</span>\n        offset = server.master_repl_offset;\n    hdr-&gt;offset = htonu64(offset);\n\n    <span class=\"hljs-comment\">/* Set the message flags. */</span>\n    <span class=\"hljs-keyword\">if</span> (nodeIsMaster(myself) &amp;&amp; server.cluster-&gt;mf_end)\n        hdr-&gt;mflags[<span class=\"hljs-number\">0</span>] |= CLUSTERMSG_FLAG0_PAUSED;\n\n    <span class=\"hljs-comment\">/* &#x8BA1;&#x7B97;&#x5E76;&#x8BBE;&#x7F6E;&#x6D88;&#x606F;&#x7684;&#x603B;&#x957F;&#x5EA6; */</span>\n    <span class=\"hljs-keyword\">if</span> (type == CLUSTERMSG_TYPE_FAIL) {\n        totlen = <span class=\"hljs-keyword\">sizeof</span>(clusterMsg)-<span class=\"hljs-keyword\">sizeof</span>(<span class=\"hljs-keyword\">union</span> clusterMsgData);\n        totlen += <span class=\"hljs-keyword\">sizeof</span>(clusterMsgDataFail);\n    } <span class=\"hljs-keyword\">else</span> <span class=\"hljs-keyword\">if</span> (type == CLUSTERMSG_TYPE_UPDATE) {\n        totlen = <span class=\"hljs-keyword\">sizeof</span>(clusterMsg)-<span class=\"hljs-keyword\">sizeof</span>(<span class=\"hljs-keyword\">union</span> clusterMsgData);\n        totlen += <span class=\"hljs-keyword\">sizeof</span>(clusterMsgDataUpdate);\n    }\n    hdr-&gt;totlen = htonl(totlen);\n}\n<span class=\"copy-code-btn\">&#x590D;&#x5236;&#x4EE3;&#x7801;</span></code></pre>\n<h3 data-id=\"heading-14\">&#x540E;&#x8BB0;</h3>\n<p>&#x672C;&#x6765;&#x53EA;&#x60F3;&#x5199;&#x4E00;&#x4E0B; Redis Cluster &#x7684; Gossip &#x534F;&#x8BAE;&#xFF0C;&#x6CA1;&#x60F3;&#x5230;&#x6587;&#x7AE0;&#x8D8A;&#x5199;&#xFF0C;&#x5185;&#x5BB9;&#x8D8A;&#x591A;&#xFF0C;&#x6700;&#x540E;&#x6E90;&#x7801;&#x5206;&#x6790;&#x4E5F;&#x662F;&#x6709;&#x70B9;&#x864E;&#x5934;&#x86C7;&#x5C3E;&#xFF0C;&#x5927;&#x5BB6;&#x5C31;&#x51D1;&#x5408;&#x770B;&#x4E00;&#x4E0B;&#xFF0C;&#x4E5F;&#x5E0C;&#x671B;&#x5927;&#x5BB6;&#x7EE7;&#x7EED;&#x5173;&#x6CE8;&#x6211;&#x540E;&#x7EED;&#x7684;&#x95EE;&#x9898;&#x3002;</p>\n<p><a href=\"http://remcarpediem.net/article/933c5a3a/\" target=\"_blank\" rel=\"nofollow noopener noreferrer\">&#x4E2A;&#x4EBA;&#x535A;&#x5BA2;&#xFF0C;&#x6B22;&#x8FCE;&#x6765;&#x73A9;</a></p>\n<p><img alt class=\"lazyload inited\" data-src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/c8e1452871e345e4b89e26c2cf550862~tplv-k3u1fbpfcp-zoom-1.image\" data-width=\"800\" data-height=\"600\" src=\"data:image/svg+xml;utf8,&lt;?xml version=&quot;1.0&quot;?&gt;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; version=&quot;1.1&quot; width=&quot;800&quot; height=&quot;600&quot;&gt;&lt;/svg&gt;\"></p><style>.markdown-body pre,.markdown-body pre>code.hljs{color:#333;background:#f8f8f8}.hljs-comment,.hljs-quote{color:#998;font-style:italic}.hljs-keyword,.hljs-selector-tag,.hljs-subst{color:#333;font-weight:700}.hljs-literal,.hljs-number,.hljs-tag .hljs-attr,.hljs-template-variable,.hljs-variable{color:teal}.hljs-doctag,.hljs-string{color:#d14}.hljs-section,.hljs-selector-id,.hljs-title{color:#900;font-weight:700}.hljs-subst{font-weight:400}.hljs-class .hljs-title,.hljs-type{color:#458;font-weight:700}.hljs-attribute,.hljs-name,.hljs-tag{color:navy;font-weight:400}.hljs-link,.hljs-regexp{color:#009926}.hljs-bullet,.hljs-symbol{color:#990073}.hljs-built_in,.hljs-builtin-name{color:#0086b3}.hljs-meta{color:#999;font-weight:700}.hljs-deletion{background:#fdd}.hljs-addition{background:#dfd}.hljs-emphasis{font-style:italic}.hljs-strong{font-weight:700}</style></div> <div class=\"image-viewer-box\" data-v-78c9b824><!----></div>');
