7;&#x6587;&#x7AE0;&#xFF0C;cxuan &#x5C31;&#x6765;&#x4E3A;&#x4F60;&#x63ED;&#x5F00;&#x8FD9;&#x4E9B;&#x7279;&#x6027;&#x80CC;&#x540E;&#x7684;&#x771F;&#x76F8;&#x3002;</p>\n<h2 data-id=\"heading-0\">&#x8BED;&#x6CD5;&#x7CD6;</h2>\n<p>&#x5728;&#x804A;&#x4E4B;&#x524D;&#x6211;&#x4EEC;&#x9700;&#x8981;&#x5148;&#x4E86;&#x89E3;&#x4E00;&#x4E0B; <code>&#x8BED;&#x6CD5;&#x7CD6;</code> &#x7684;&#x6982;&#x5FF5;&#xFF1A;<code>&#x8BED;&#x6CD5;&#x7CD6;&#xFF08;Syntactic sugar&#xFF09;</code>&#xFF0C;&#x4E5F;&#x53EB;&#x505A;&#x7CD6;&#x8863;&#x8BED;&#x6CD5;&#xFF0C;&#x662F;&#x82F1;&#x56FD;&#x79D1;&#x5B66;&#x5BB6;&#x53D1;&#x660E;&#x7684;&#x4E00;&#x4E2A;&#x672F;&#x8BED;&#xFF0C;&#x901A;&#x5E38;&#x6765;&#x8BF4;&#x4F7F;&#x7528;&#x8BED;&#x6CD5;&#x7CD6;&#x80FD;&#x591F;&#x589E;&#x52A0;&#x7A0B;&#x5E8F;&#x7684;<code>&#x53EF;&#x8BFB;&#x6027;</code>&#xFF0C;&#x4ECE;&#x800C;&#x51CF;&#x5C11;&#x7A0B;&#x5E8F;&#x4EE3;&#x7801;&#x51FA;&#x9519;&#x7684;&#x673A;&#x4F1A;&#xFF0C;&#x771F;&#x662F;&#x53C8;&#x9999;&#x53C8;&#x751C;&#x3002;</p>\n<p>&#x8BED;&#x6CD5;&#x7CD6;&#x6307;&#x7684;&#x662F;&#x8BA1;&#x7B97;&#x673A;&#x8BED;&#x8A00;&#x4E2D;&#x6DFB;&#x52A0;&#x7684;&#x67D0;&#x79CD;&#x8BED;&#x6CD5;&#xFF0C;<strong>&#x8FD9;&#x79CD;&#x8BED;&#x6CD5;&#x5BF9;&#x8BED;&#x8A00;&#x7684;&#x529F;&#x80FD;&#x5E76;&#x6CA1;&#x6709;&#x5F71;&#x54CD;&#xFF0C;&#x4F46;&#x662F;&#x66F4;&#x65B9;&#x4FBF;&#x7A0B;&#x5E8F;&#x5458;&#x4F7F;&#x7528;</strong>&#x3002;&#x56E0;&#x4E3A; Java &#x4EE3;&#x7801;&#x9700;&#x8981;&#x8FD0;&#x884C;&#x5728; JVM &#x4E2D;&#xFF0C;<strong>JVM &#x662F;&#x5E76;&#x4E0D;&#x652F;&#x6301;&#x8BED;&#x6CD5;&#x7CD6;&#x7684;&#xFF0C;&#x8BED;&#x6CD5;&#x7CD6;&#x5728;&#x7A0B;&#x5E8F;&#x7F16;&#x8BD1;&#x9636;&#x6BB5;&#x5C31;&#x4F1A;&#x88AB;&#x8FD8;&#x539F;&#x6210;&#x7B80;&#x5355;&#x7684;&#x57FA;&#x7840;&#x8BED;&#x6CD5;&#x7ED3;&#x6784;&#xFF0C;&#x8FD9;&#x4E2A;&#x8FC7;&#x7A0B;&#x5C31;&#x662F;<code>&#x89E3;&#x8BED;&#x6CD5;&#x7CD6;</code></strong>&#x3002;&#x6240;&#x4EE5;&#x5728; Java &#x4E2D;&#xFF0C;&#x771F;&#x6B63;&#x652F;&#x6301;&#x8BED;&#x6CD5;&#x7CD6;&#x7684;&#x662F; Java &#x7F16;&#x8BD1;&#x5668;&#xFF0C;&#x771F;&#x662F;&#x6362;&#x6C64;&#x4E0D;&#x6362;&#x836F;&#xFF0C;&#x4E07;&#x53D8;&#x4E0D;&#x79BB;&#x5176;&#x5B97;&#xFF0C;&#x5173;&#x4E86;&#x706F;&#x90FD;&#x4E00;&#x6837;&#x3002;&#x3002;&#x3002;&#x3002;&#x3002;&#x3002;</p>\n<p>&#x4E0B;&#x9762;&#x6211;&#x4EEC;&#x5C31;&#x6765;&#x8BA4;&#x8BC6;&#x4E00;&#x4E0B; Java &#x4E2D;&#x7684;&#x8FD9;&#x4E9B;&#x8BED;&#x6CD5;&#x7CD6;</p>\n<h3 data-id=\"heading-1\">&#x6CDB;&#x578B;</h3>\n<p>&#x6CDB;&#x578B;&#x662F;&#x4E00;&#x79CD;&#x8BED;&#x6CD5;&#x7CD6;&#x3002;&#x5728; JDK1.5 &#x4E2D;&#xFF0C;&#x5F15;&#x5165;&#x4E86;&#x6CDB;&#x578B;&#x673A;&#x5236;&#xFF0C;&#x4F46;&#x662F;&#x6CDB;&#x578B;&#x673A;&#x5236;&#x7684;&#x672C;&#x8EAB;&#x662F;&#x901A;&#x8FC7;<code>&#x7C7B;&#x578B;&#x64E6;&#x9664;</code> &#x6765;&#x5B9E;&#x73B0;&#x7684;&#xFF0C;&#x5728; JVM &#x4E2D;&#x6CA1;&#x6709;&#x6CDB;&#x578B;&#xFF0C;&#x53EA;&#x6709;&#x666E;&#x901A;&#x7C7B;&#x578B;&#x548C;&#x666E;&#x901A;&#x65B9;&#x6CD5;&#xFF0C;&#x6CDB;&#x578B;&#x7C7B;&#x7684;&#x7C7B;&#x578B;&#x53C2;&#x6570;&#xFF0C;&#x5728;&#x7F16;&#x8BD1;&#x65F6;&#x90FD;&#x4F1A;&#x88AB;&#x64E6;&#x9664;&#x3002;&#x6CDB;&#x578B;&#x5E76;&#x6CA1;&#x6709;&#x81EA;&#x5DF1;&#x72EC;&#x7279;&#x7684; Class&#x7C7B;&#x578B;&#x3002;&#x5982;&#x4E0B;&#x4EE3;&#x7801;&#x6240;&#x793A;</p>\n<pre><code class=\"hljs language-java copyable\" lang=\"java\">List&lt;Integer&gt; aList = <span class=\"hljs-keyword\">new</span> ArrayList();\nList&lt;String&gt; bList = <span class=\"hljs-keyword\">new</span> ArrayList();\n\nSystem.out.println(aList.getClass() == bList.getClass());\n<span class=\"copy-code-btn\">&#x590D;&#x5236;&#x4EE3;&#x7801;</span></code></pre>\n<p><code>List&lt;Ineger&gt;</code> &#x548C; <code>List&lt;String&gt;</code> &#x88AB;&#x8BA4;&#x4E3A;&#x662F;&#x4E0D;&#x540C;&#x7684;&#x7C7B;&#x578B;&#xFF0C;&#x4F46;&#x662F;&#x8F93;&#x51FA;&#x5374;&#x5F97;&#x5230;&#x4E86;&#x76F8;&#x540C;&#x7684;&#x7ED3;&#x679C;&#xFF0C;&#x8FD9;&#x662F;&#x56E0;&#x4E3A;&#xFF0C;<strong>&#x6CDB;&#x578B;&#x4FE1;&#x606F;&#x53EA;&#x5B58;&#x5728;&#x4E8E;&#x4EE3;&#x7801;&#x7F16;&#x8BD1;&#x9636;&#x6BB5;&#xFF0C;&#x5728;&#x8FDB;&#x5165; JVM &#x4E4B;&#x524D;&#xFF0C;&#x4E0E;&#x6CDB;&#x578B;&#x76F8;&#x5173;&#x7684;&#x4FE1;&#x606F;&#x4F1A;&#x88AB;&#x64E6;&#x9664;&#x6389;&#xFF0C;&#x4E13;&#x4E1A;&#x672F;&#x8BED;&#x53EB;&#x505A;&#x7C7B;&#x578B;&#x64E6;&#x9664;</strong>&#x3002;&#x4F46;&#x662F;&#xFF0C;&#x5982;&#x679C;&#x5C06;&#x4E00;&#x4E2A; Integer &#x7C7B;&#x578B;&#x7684;&#x6570;&#x636E;&#x653E;&#x5165;&#x5230;  <code>List&lt;String&gt;</code> &#x4E2D;&#x6216;&#x8005;&#x5C06;&#x4E00;&#x4E2A; String &#x7C7B;&#x578B;&#x7684;&#x6570;&#x636E;&#x653E;&#x5728; <code>List&lt;Ineger&gt;</code> &#x4E2D;&#x662F;&#x4E0D;&#x5141;&#x8BB8;&#x7684;&#x3002;</p>\n<p>&#x5982;&#x4E0B;&#x56FE;&#x6240;&#x793A;</p>\n<p><img alt class=\"lazyload inited\" data-src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/22d08d5b23184cfdbb886956cd4f7397~tplv-k3u1fbpfcp-zoom-1.image\" data-width=\"800\" data-height=\"600\" src=\"data:image/svg+xml;utf8,&lt;?xml version=&quot;1.0&quot;?&gt;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; version=&quot;1.1&quot; width=&quot;800&quot; height=&quot;600&quot;&gt;&lt;/svg&gt;\"></p>\n<p>&#x65E0;&#x6CD5;&#x5C06;&#x4E00;&#x4E2A; Integer &#x7C7B;&#x578B;&#x7684;&#x6570;&#x636E;&#x653E;&#x5728; <code>List&lt;String&gt;</code> &#x548C;&#x65E0;&#x6CD5;&#x5C06;&#x4E00;&#x4E2A; String &#x7C7B;&#x578B;&#x7684;&#x6570;&#x636E;&#x653E;&#x5728; <code>List&lt;Integer&gt;</code> &#x4E2D;&#x662F;&#x4E00;&#x6837;&#x4F1A;&#x7F16;&#x8BD1;&#x5931;&#x8D25;&#x3002;</p>\n<h3 data-id=\"heading-2\">&#x81EA;&#x52A8;&#x62C6;&#x7BB1;&#x548C;&#x81EA;&#x52A8;&#x88C5;&#x7BB1;</h3>\n<p>&#x81EA;&#x52A8;&#x62C6;&#x7BB1;&#x548C;&#x81EA;&#x52A8;&#x88C5;&#x7BB1;&#x662F;&#x4E00;&#x79CD;&#x8BED;&#x6CD5;&#x7CD6;&#xFF0C;&#x5B83;&#x8BF4;&#x7684;&#x662F;&#x516B;&#x79CD;&#x57FA;&#x672C;&#x6570;&#x636E;&#x7C7B;&#x578B;&#x7684;&#x5305;&#x88C5;&#x7C7B;&#x548C;&#x5176;&#x57FA;&#x672C;&#x6570;&#x