#x6570;&#xFF0C;&#x63A5;&#x7740;&#x8F93;&#x51FA;&#x7ED3;&#x679C;&#x3002;&#x5F53;&#x6709;&#x4E00;&#x4E32;&#x6570;&#x636E;&#x65F6;&#xFF0C;&#x6211;&#x4EEC;&#x9700;&#x8981;&#x601D;&#x8003;&#x5982;&#x4F55;&#x901A;&#x8FC7;&#x51FD;&#x6570;&#x7684;&#x5F62;&#x5F0F;&#x6765;&#x8868;&#x8FBE;&#xFF0C;&#x800C;&#x4E0D;&#x662F;&#x4E00;&#x884C;&#x4E00;&#x884C;&#x7684;&#x547D;&#x4EE4;&#x5F0F;&#x7F16;&#x7A0B;&#xFF0C;&#x4E3E;&#x4E2A;&#x4F8B;&#x5B50;&#xFF1A;</p>\n<pre><code class=\"copyable\">&#x975E;&#x51FD;&#x6570;&#x5F0F;&#x7F16;&#x7A0B;\n\nvar name = &quot;Anjana&quot;\nvar greeting = &quot;Hi i&apos;m&quot;\nconsole.log(greeting + name)\n// Hi i&apos;m Anjana\n\n<span class=\"copy-code-btn\">&#x590D;&#x5236;&#x4EE3;&#x7801;</span></code></pre>\n<p>&#x8FD9;&#x5C31;&#x662F;&#x5178;&#x578B;&#x7684;&#x547D;&#x4EE4;&#x5F0F;&#x7F16;&#x7A0B;&#xFF0C;&#x5148;&#x5904;&#x7406;&#x7B2C;&#x4E00;&#x884C;&#xFF0C;&#x63A5;&#x7740;&#x5904;&#x7406;&#x7B2C;&#x4E8C;&#x884C;&#x3002;&#x518D;&#x6765;&#x770B;&#x770B;&#x7528;&#x51FD;&#x6570;&#x5F0F;&#x6765;&#x8868;&#x8FBE;&#x8FD9;&#x4E32;&#x4EE3;&#x7801;&#xFF0C;&#x975E;&#x5E38;&#x7B80;&#x6D01;&#x6613;&#x61C2;&#xFF1A;</p>\n<pre><code class=\"copyable\">function greeting(name) {\n	return &quot;Hi i&apos;m &quot; + name\n}\n\ngreeting(&quot;Anjana&quot;)\n\n<span class=\"copy-code-btn\">&#x590D;&#x5236;&#x4EE3;&#x7801;</span></code></pre>\n<h2 data-id=\"heading-1\">&#x7EAF;&#x51FD;&#x6570;&#x548C;&#x526F;&#x4F5C;&#x7528;</h2>\n<p>&#x51FD;&#x6570;&#x5F0F;&#x7F16;&#x7A0B;&#x7684;&#x6838;&#x5FC3;&#x662F;&#x4F7F;&#x7528;&#x7EAF;&#x51FD;&#x6570;&#xFF08;pure function&#xFF09;&#x6765;&#x907F;&#x514D;&#x526F;&#x4F5C;&#x7528;&#xFF08;pure effects&#xFF09;&#x3002;&#x7EAF;&#x51FD;&#x6570;&#x662F;&#x6307;&#xFF0C;&#x53EA;&#x4F7F;&#x7528;&#x4F20;&#x5165;&#x7684;&#x53C2;&#x6570;&#x6765;&#x5904;&#x7406;&#x8F93;&#x51FA;&#x7684;&#x7ED3;&#x679C;&#xFF0C;&#x8FD9;&#x6837;&#x7684;&#x597D;&#x5904;&#x662F;&#x6BCF;&#x6B21;&#x4F20;&#x5165;&#x76F8;&#x540C;&#x7684;&#x53C2;&#x6570;&#xFF0C;&#x8FD4;&#x56DE;&#x7684;&#x5185;&#x5BB9;&#x90FD;&#x662F;&#x76F8;&#x540C;&#x7684;&#x3002;&#x526F;&#x4F5C;&#x7528;&#x662F;&#x6307;&#x51FD;&#x6570;&#x4F7F;&#x7528;&#x4E86;&#x5168;&#x5C40;&#x53D8;&#x91CF;&#x6216;&#x8005;&#x6CA1;&#x6709;&#x8FD4;&#x56DE;&#x7ED3;&#x679C;&#xFF0C;&#x5BFC;&#x81F4;&#x51FD;&#x6570;&#x4E0D;&#x53EF;&#x63A7;&#x3002;&#xFF08;&#x7EAF;&#x51FD;&#x6570;&#x5173;&#x6CE8;&#x7684;&#x662F;&#x51FD;&#x6570;&#x5185;&#x5BB9;&#x7684;&#x5904;&#x7406;&#x4EE5;&#x53CA;&#x8FD4;&#x56DE;&#x7684;&#x7ED3;&#x679C;&#xFF0C;&#x6240;&#x4EE5;&#x51FD;&#x6570;&#x5185;&#x90E8;&#x4EC5;&#x4EC5;&#x6709;console&#x4E5F;&#x4E0D;&#x80FD;&#x7B97;&#x7EAF;&#x51FD;&#x6570;&#xFF0C;&#x867D;&#x7136;&#x4ED6;&#x8FD4;&#x56DE;&#x7684;&#x662F;void&#xFF09;</p>\n<pre><code class=\"copyable\">function print(){\n	console.log(&quot;test&quot;)\n}\n\n&#x6216;\nvar name = &quot;Anjana&quot;\nfunction getName(){\n	return name\n}\n\n<span class=\"copy-code-btn\">&#x590D;&#x5236;&#x4EE3;&#x7801;</span></code></pre>\n<p><code>print</code>&#x548C;<code>getName</code>&#x8FD9;&#x4E24;&#x4E2A;&#x51FD;&#x6570;&#x90FD;&#x4E0D;&#x662F;&#x7EAF;&#x51FD;&#x6570;&#x3002;</p>\n<h2 data-id=\"heading-2\">&#x9AD8;&#x9636;&#x51FD;&#x6570;</h2>\n<p>&#x51FD;&#x6570;&#x5F0F;&#x7F16;&#x7A0B;&#x7B2C;&#x4E8C;&#x4E2A;&#x5173;&#x952E;&#x70B9;&#x5C31;&#x662F;&#x9AD8;&#x9636;&#x51FD;&#x6570;&#xFF0C;&#x9AD8;&#x9636;&#x51FD;&#x6570;&#x662F;&#x6307;&#x53EF;&#x4EE5;&#x5C06;&#x51FD;&#x6570;&#x4F5C;&#x4E3A;&#x53C2;&#x6570;&#x4F20;&#x5165;&#x53E6;&#x4E00;&#x4E2A;&#x51FD;&#x6570;&#xFF0C;&#x6216;&#x8005;&#x5728;&#x4E00;&#x4E2A;&#x51FD;&#x6570;&#x4E2D;&#x8FD4;&#x56DE;&#x53E6;&#x4E00;&#x4E2A;&#x51FD;&#x6570;&#xFF1A;</p>\n<pre><code class=\"copyable\">function makeAdjectifier(adjective) {\n	return function(string) {\n    	return ajective + &apos; &apos; + string\n    }\n}\n\nvar coolifier = makeAdjectifier(&quot;cool&quot;)\ncoolifier(&quot;conference&quot;)\n\n// cool conference\n\n<span class=\"copy-code-btn\">&#x590D;&#x5236;&#x4EE3;&#x7801;</span></code></pre>\n<h2 data-id=\"heading-3\">&#x4E0D;&#x8981;&#x4F7F;&#x7528;&#x904D;&#x5386;&#xFF08;no iterate&#xFF09;</h2>\n<p>&#x5728;&#x51FD;&#x6570;&#x5F0F;&#x7F16;&#x7A0B;&#x4E2D;&#xFF0C;&#x4E0D;&#x8981;&#x4F7F;&#x7528;<code>for</code>&#x6216;&#x8005;<code>while</code>&#x5FAA;&#x73AF;&#xFF0C;&#x800C;&#x662F;&#x4F7F;&#x7528;&#x9AD8;&#x9636;&#x51FD;&#x6570;&#x6BD4;&#x5982;<code>map</code>&#xFF0C;<code>reduce</code>&#xFF0C;<code>filter</code>&#x3002;&#x8FD9;&#x91CC;&#x6211;&#x4E0D;&#x4F1A;&#x8BE6;&#x7EC6;&#x4ECB;&#x7ECD;&#x5173;&#x6CE8;&#x8FD9;&#x4E09;&#x4E2A;&#x51FD;&#x6570;&#x7684;&#x4F7F;&#x7528;&#x65B9;&#x6CD5;&#xFF0C;&#x4F46;&#x6211;&#x4F1A;&#x4F7F;&#x7528;&#x4E00;&#x5F20;&#x56FE;&#x6765;&#x544A;&#x8BC9;&#x4F60;&#x4EC0;&#x4E48;&#x662F;<code>map</code>&#x548C;<code>reduce</code>&#xFF1A;</p>\n<p><img alt class=\"lazyload inited\" data-src=\"https://p1-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/9e214dce9156426b91330adf2c3a3aa6~tplv-k3u1fbpfcp-watermark.image\" data-width=\"800\" data-height=\"600\" src=\"data:image/svg+xml;utf8,&lt;?xml version=&quot;1.0&quot;?&gt;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; version=&quot;1.1&quot; width=&quot;800&quot; height=&quot;600&quot;&gt;&lt;/svg&gt;\"></p>\n<p>&#xFF08;&#x8FD9;&#x5F20;&#x56FE;&#x771F;&#x7684;&#x5F88;&#x5F62;&#x8C61;&#x4E86;&#xFF0C;youtube&#x5E95;&#x4E0B;&#x7684;&#x8BC4;&#x8BBA;&#x4E5F;&#x5728;&#x8BF4;&#x8FD9;&#x5F20;&#x56FE;&#x5F62;&#x5BB9;&#x7684;&#x975E;&#x5E38;&#x597D;&#xFF0C;&#x4ED4;&#x7EC6;&#x60F3;&#x60F3;&#x786E;&#x5B9E;&#x50CF;&#x8FD9;&#x4E48;&#x56DE;&#x4E8B;&#x513F;&#xFF09;</p>\n<h2 data-id=\"heading-4\">&#x907F;&#x514D;&#x53EF;&#x53D8;&#x6570;&#x636E;</h2>\n<p>&#x5728;&#x5B9E;&#x9645;&#x5F00;&#x53D1;&#x4E2D;&#xFF0C;&#x6211;&#x4EEC;&#x7ECF;&#x5E38;&#x4F1A;&#x9047;&#x5230;&#x6539;&#x53D8;&#x6570;&#x636E;&#x5185;&#x5BB9;&#x7684;&#x60C5;&#x51B5;&#xFF1A;</p>\n<pre><code class=\"copyable\">var rooms = [&quot;H1&quot;, &quot;H2&quot;, &quot;H3&quot;]\nrooms[2] = &quot;H4&quot;\nconsole.log(rooms)\n=&gt; [&quot;H1&quot;,&quot;H2&quot;,&quot;H4&quot;]\n<span class=\"copy-code-btn\">&#x590D;&#x5236;&#x4EE3;&#x7801;</span></code></pre>\n<p>&#x5728;&#x8FD9;&#x4E2A;&#x4F8B;&#x5B50;&#x4E2D;&#xFF0C;&#x6211;&#x4EEC;&#x5904;&#x4E8E;&#x5404;&#x79CD;&#x539F;&#x56E0;&#xFF0C;&#x8981;&#x4FEE;&#x6539;&#x6570;&#x7EC4;<code>rooms</code>&#x4E2D;&#x7684;<code>H3</code>&#xFF0C;&#x4E8E;&#x662F;&#x6211;&#x4EEC;&#x76F4;&#x63A5;&#x4FEE;&#x6539;&#x4E86;&#x6570;&#x7EC4;&#x7684;&#x7B2C;&#x4E8C;&#x9879;&#x3002;&#x8981;&#x6CE8;&#x610F;&#x7684;&#x662F;&#x8FD9;&#x6837;&#x4F1A;&#x5BFC;&#x81F4;&#x5404;&#x79CD;&#x5404;&#x6837;&#x4E0D;&#x53EF;&#x9884;&#x89C1;&#x7684;&#x95EE;&#x9898;&#xFF0C;&#x7279;&#x522B;&#x662F;&#x5728;&#x534F;&#x4F5C;&#x5F00;&#x53D1;&#x7684;&#x8FC7;&#x7A0B;&#x4E2D;&#xFF0C;&#x5176;&#x5B83;&#x4EBA;&#x53EF;&#x80FD;&#x5E76;&#x4E0D;&#x77E5;&#x9053;&#x6211;&#x4EEC;&#x4FEE;&#x6539;&#x4E86;&#x6570;&#x7EC4;&#xFF0C;debug&#x8D77;&#x6765;&#x4F1A;&#x975E;&#x5E38;&#x7684;&#x5934;&#x75BC;&#x3002;</p>\n<p>&#x66F4;&#x597D;&#x7684;&#x89E3;&#x51B3;&#x529E;&#x6CD5;&#x662F;&#x5C06;<code>rooms</code>&#x53D8;&#x4E3A;&#x4E0D;&#x53EF;&#x53D8;&#x7684;&#x6570;&#x636E;&#xFF0C;&#x8FD9;&#x6837;&#x65E0;&#x8BBA;&#x662F;&#x8C01;&#xFF0C;&#x4EC0;&#x4E48;&#x65F6;&#x5019;&#x4F7F;&#x7528;<code>rooms</code>&#xFF0C;&#x4ED6;&#x8FD8;&#x662F;&#x4E4B;&#x524D;&#x7684;&#x6837;&#x5B50;&#x3002;</p>\n<pre><code class=\"copyable\">var rooms = [&quot;H1&quot;, &quot;H2&quot;, &quot;H3&quot;]\nvar newRooms = rooms.map(item =&gt; {\n	if(item === &quot;H3&quot;) return &quot;H4&quot;\n    else return item\n})\n\nnewRooms =&gt; [&quot;H1&quot;, &quot;H2&quot;, &quot;H4&quot;]\nrooms =&gt; [&quot;H1&quot;, &quot;H2&quot;, &quot;H3&quot;]\n<span class=\"copy-code-btn\">&#x590D;&#x5236;&#x4EE3;&#x7801;</span></code></pre>\n<p>&#x901A;&#x8FC7;&#x8FD9;&#x79CD;&#x65B9;&#x5F0F;&#xFF0C;&#x53EF;&#x4EE5;&#x4FDD;&#x8BC1;<code>rooms</code>&#x662F;&#x4E0D;&#x53EF;&#x53D8;&#x7684;&#xFF0C;&#x8FD9;&#x4E5F;&#x4F1A;&#x964D;&#x4F4E;&#x4EE3;&#x7801;&#x7684;BUG&#x7387;&#x3002;&#x8FD9;&#x65F6;&#x4F60;&#x53EF;&#x80FD;&#x4F1A;&#x95EE;&#xFF0C;&#x5982;&#x679C;&#x6211;&#x7684;&#x6570;&#x636E;&#x91CF;&#x975E;&#x5E38;&#x5927;&#xFF0C;&#x8FD9;&#x79CD;copy&#x4F1A;&#x975E;&#x5E38;&#x5F71;&#x54CD;&#x6027;&#x80FD;&#x3002;</p>\n<p>&#x6CA1;&#x9519;&#xFF0C;&#x8BF7;&#x7EE7;&#x7EED;&#x770B;&#x3002;</p>\n<h2 data-id=\"heading-5\">&#x901A;&#x8FC7;&#x6301;&#x4E45;&#x5316;&#x6570;&#x636E;&#x7ED3;&#x6784;&#x51CF;&#x5C11;&#x4E0D;&#x53EF;&#x53D8;&#x6570;&#x636E;&#x7684;&#x6027;&#x80FD;&#x95EE;&#x9898;</h2>\n<p>&#x6B63;&#x5982;&#x524D;&#x6587;&#x6240;&#x8BF4;&#xFF0C;&#x5982;&#x679C;&#x6211;&#x6709;&#x4E00;&#x4E2A;&#x5F88;&#x590D;&#x6742;&#x7684;&#x6570;&#x636E;&#xFF0C;&#x800C;&#x6211;&#x4EC5;&#x4EC5;&#x4FEE;&#x6539;&#x5176;&#x4E2D;&#x4E00;&#x70B9;&#x70B9;&#x6570;&#x636E;&#xFF0C;&#x6211;&#x8FD8;&#x8981;&#x91CD;&#x65B0;&#x590D;&#x5236;&#x4E00;&#x4E2A;&#x6570;&#x636E;&#x6765;&#x5904;&#x7406;&#xFF0C;&#x662F;&#x975E;&#x5E38;&#x5F71;&#x54CD;&#x6027;&#x80FD;&#x7684;&#x3002;&#x800C;&#x901A;&#x8FC7;&#x6301;&#x4E45;&#x5316;&#x6570;&#x636E;&#x7ED3;&#x6784;&#xFF0C;&#x662F;&#x53EF;&#x4EE5;&#x89E3;&#x51B3;&#x8FD9;&#x4E2A;&#x95EE;&#x9898;&#x7684;&#xFF0C;&#x6211;&#x4EEC;&#x6765;&#x770B;&#x770B;&#x5B83;&#x662F;&#x5982;&#x4F55;&#x5DE5;&#x4F5C;&#x7684;&#x3002;</p>\n<p>&#x8FD8;&#x662F;&#x62FF;&#x4E4B;&#x524D;&#x7684;&#x4F8B;&#x5B50;&#xFF0C;&#x6211;&#x4EEC;&#x590D;&#x5236;&#x4E86;rooms&#xFF0C;&#x90A3;&#x5728;&#x5185;&#x5B58;&#x4E2D;&#x5B9E;&#x9645;&#x5C31;&#x6709;&#x4E86;&#x4E24;&#x4E2A;&#x6570;&#x7EC4;&#xFF1A;</p>\n<p><img alt class=\"lazyload inited\" data-src=\"https://p6-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/2436ce3b5e8544e88a829a5ef062972a~tplv-k3u1fbpfcp-watermark.image\" data-width=\"800\" data-height=\"600\" src=\"data:image/svg+xml;utf8,&lt;?xml version=&quot;1.0&quot;?&gt;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; version=&quot;1.1&quot; width=&quot;800&quot; height=&quot;600&quot;&gt;&lt;/svg&gt;\"></p>\n<p>&#x8FD9;&#x597D;&#x5417;&#xFF1F;&#x8FD9;&#x4E0D;&#x597D;&#x3002;</p>\n<p>&#x6240;&#x4EE5;&#xFF0C;&#x6301;&#x4E45;&#x5316;&#x6570;&#x636E;&#x7ED3;&#x6784;&#x7684;&#x5904;&#x7406;&#x65B9;&#x6CD5;&#x662F;&#x8FD9;&#x6837;&#x7684;&#xFF1A;</p>\n<p><img alt class=\"lazyload inited\" data-src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/ca0717cfe6224d099e354d35c24b467d~tplv-k3u1fbpfcp-watermark.image\" data-width=\"800\" data-height=\"600\" src=\"data:image/svg+xml;utf8,&lt;?xml version=&quot;1.0&quot;?&gt;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; version=&quot;1.1&quot; width=&quot;800&quot; height=&quot;600&quot;&gt;&lt;/svg&gt;\"></p>\n<p>&#x5C06;&#x6570;&#x7EC4;&#x7684;&#x6BCF;&#x4E00;&#x9879;&#x4F5C;&#x4E3A;&#x4E00;&#x68F5;&#x6811;&#x7684;&#x5B50;&#x8282;&#x70B9;&#xFF08;node&#xFF09;&#xFF0C;&#x5F53;&#x6211;&#x4EEC;&#x8981;&#x4FEE;&#x6539;&#x67D0;&#x4E00;&#x9879;&#x7684;&#x65F6;&#x5019;&#xFF0C;&#x53EA;&#x9700;&#x8981;&#x521B;&#x5EFA;&#x4E00;&#x4E2A;&#x65B0;&#x8282;&#x70B9;&#xFF0C;&#x53BB;&#x8FDE;&#x63A5;&#x5230;&#x8FD9;&#x9897;&#x6811;&#x4E2D;&#x5373;&#x53EF;&#xFF0C;&#x8FD9;&#x6837;&#x65E2;&#x590D;&#x7528;&#x4E86;&#x4E0D;&#x9700;&#x8981;&#x6539;&#x53D8;&#x7684;&#x6570;&#x636E;&#xFF0C;&#x51CF;&#x5C11;&#x6027;&#x80FD;&#x7684;&#x635F;&#x8017;&#xFF0C;&#x540C;&#x65F6;&#x4E5F;&#x4FDD;&#x8BC1;&#x4E86;&#x539F;&#x59CB;&#x6570;&#x636E;&#x7684;&#x4E0D;&#x53EF;&#x53D8;&#x3002;</p>\n<p>&#x5F53;&#x7136;&#xFF0C;&#x5728;JavaScript&#x4E2D;&#x6211;&#x4EEC;&#x4E5F;&#x4E0D;&#x9700;&#x8981;&#x4E3A;&#x6B64;&#x65E9;&#x8F6E;&#x5B50;&#xFF0C;&#x5DF2;&#x7ECF;&#x6709;&#x5F88;&#x591A;&#x6210;&#x719F;&#x7684;&#x5E93;&#x4F9B;&#x6211;&#x4EEC;&#x4F7F;&#x7528;&#x3002;&#x6BD4;&#x5982;&#x89C6;&#x9891;&#x4E2D;&#x4F5C;&#x8005;&#x63A8;&#x8350;&#x7684;<a href=\"https://github.com/swannodette/mori\" target=\"_blank\" rel=\"nofollow noopener noreferrer\">Mori</a>&#xFF0C;&#x4E0D;&#x8FC7;&#x6211;&#x66F4;&#x63A8;&#x8350;Facebook&#x7684;<a href=\"https://github.com/immutable-js/immutable-js\" target=\"_blank\" rel=\"nofollow noope